// Capturamos el texto desde la propiedad correcta
const texto = String($input.first().json.output || '').trim();

// Regex para extraer "producto" cantidad
const regex = /"([^"]+)"\s+(\d+(?:\.\d+)?)/g;

const filas = [];
let match;

while ((match = regex.exec(texto)) !== null) {
  filas.push({
    PRODUCTO: match[1].replace(/_/g, ' '),
    CANTIDAD: parseFloat(match[2]),
    FECHA: new Date().toLocaleString('es-CO', { timeZone: 'America/Bogota' })
  });
}

return filas;

// Obtenemos todas las ventas desde Code_ventas
const ventas = $('Code_ventas').all().map(item => item.json);

// Obtenemos la lista de precios desde Obtener_lista_precios
const precios = $('Obtener_lista_precios').all().map(item => item.json);





// Funci√≥n para buscar el precio y costo de un producto
function buscarPrecio(nombreProducto) {
  const nombreVenta = nombreProducto.toLowerCase().replace(/_/g, ' ').trim();

  for (const p of precios) {
    const nombrePrecio = p.PRODUCTO.toLowerCase().replace(/_/g, ' ').trim();
    if (nombrePrecio === nombreVenta) {
      return {
        precio: parseFloat(p.PRECIO_UNITARIO),
        costo: parseFloat(p.COSTO || 0),
        error: false,
        productoEncontrado: p.PRODUCTO
      };
    }
  }

  // Si no se encuentra, devuelve error
  return { 
    precio: 0, 
    costo: 0, 
    error: true,
    productoEncontrado: null
  };
}

// Procesamos todas las ventas
return ventas.map(v => {
  const resultado = buscarPrecio(v.PRODUCTO);

  if (resultado.error) {
    return {
      json: {
        PRODUCTO: v.PRODUCTO,
        CANTIDAD: Number(v.CANTIDAD),
        VALOR_UNITARIO: 0,
        VALOR_TOTAL: 0,
        COSTO: 0,
        FECHA: v.FECHA,
        ERROR: true,
        MENSAJE_ERROR: `${v.PRODUCTO}: No existe en el inventario. AGREGA el APODO ${v.PRODUCTO} al PRODUCTO que pertenezca en el siguiente link : https://docs.google.com/spreadsheets/d/1vkj-0XG2xG8AixToCz2oy_e6dbo8a7Nf500tXRg7W2A/edit?usp=sharing`
      }
    };
  }

  const valorTotal = Number(v.CANTIDAD) * resultado.precio;

  return {
    json: {
      PRODUCTO: v.PRODUCTO,
      CANTIDAD: Number(v.CANTIDAD),
      VALOR_UNITARIO: resultado.precio,
      VALOR_TOTAL: valorTotal,
      COSTO: resultado.costo,
      FECHA: v.FECHA,
      ERROR: false,
      MENSAJE: null
    }
  };
});

// C√≥digo para nodo Code de n8n - Confirmaci√≥n de Ventas (con STOCK_ACTUAL_UNIDADES)
// Obtener los datos de entrada
const inputData = $input.all();

// Arrays para procesar los datos
const ventasItems = [];
let totalGeneral = 0;

// Productos que no deben mostrar stock
const productosExcluidosStock = [
  'arepas de queso',
  'arepa de queso',
  'chorizo',
  'chorizos',
  'arepa burguer'
];

// Funci√≥n para verificar si un producto debe excluir el stock
const debeExcluirStock = (producto) => {
  const productoLower = producto.toLowerCase().trim();
  return productosExcluidosStock.some(excluido => 
    productoLower.includes(excluido) || excluido.includes(productoLower)
  );
};

// Funci√≥n para manejar plurales
const manejarPlural = (cantidad, producto) => {
  const cantidadNum = parseInt(cantidad);
  if (cantidadNum === 1) {
    const palabras = producto.trim().split(' ');
    let primeraPalabra = palabras[0];
    if (primeraPalabra.endsWith('s') && primeraPalabra.length > 1) {
      const excepcionesS = ['lapis', 'virus', 'tesis', 'crisis', 'oasis', 'martes', 'miercoles', 'jueves', 'viernes'];
      const palabraLower = primeraPalabra.toLowerCase();
      if (!excepcionesS.some(excepcion => palabraLower.includes(excepcion))) {
        if (primeraPalabra.endsWith('es') && primeraPalabra.length > 2) {
          const antesDe_es = primeraPalabra[primeraPalabra.length - 3];
          if (!'aeiou√°√©√≠√≥√∫'.includes(antesDe_es.toLowerCase())) {
            primeraPalabra = primeraPalabra.slice(0, -2);
          } else {
            primeraPalabra = primeraPalabra.slice(0, -1);
          }
        } else {
          primeraPalabra = primeraPalabra.slice(0, -1);
        }
      }
    }
    palabras[0] = primeraPalabra;
    return palabras.join(' ');
  }
  return producto;
};

// Procesar cada fila de datos
for (const item of inputData) {
  const { 
    CANTIDAD, 
    PRODUCTO, 
    VALOR_UNITARIO, 
    VALOR_TOTAL,
    STOCK_ACTUAL_UNIDADES
  } = item.json;

  // Validar que todos los campos necesarios est√©n presentes
  if (!CANTIDAD || !PRODUCTO || !VALOR_TOTAL || STOCK_ACTUAL_UNIDADES == null) {
    console.log('Datos incompletos en una fila:', item.json);
    continue;
  }

  // Formatear valores monetarios
  const formatCurrency = (value) => {
    return Number(value).toLocaleString('es-CO');
  };

  // Manejar plural/singular del producto
  const productoFormateado = manejarPlural(CANTIDAD, PRODUCTO);

  // Crear item con o sin stock seg√∫n el producto
  let itemFormateado;
  if (debeExcluirStock(PRODUCTO)) {
    itemFormateado = `- ${parseInt(CANTIDAD)} ${productoFormateado} = ${formatCurrency(VALOR_TOTAL)}`;
  } else {
    itemFormateado = `- ${parseInt(CANTIDAD)} ${productoFormateado} = ${formatCurrency(VALOR_TOTAL)} (Stock actual: ${STOCK_ACTUAL_UNIDADES})`;
  }

  ventasItems.push(itemFormateado);

  // Sumar al total general
  totalGeneral += Number(VALOR_TOTAL);
}

// Formatear el total general
const totalFormateado = totalGeneral.toLocaleString('es-CO');

// Crear mensaje con formato HTML para Telegram
let mensajeHTML = '<b>‚úÖ Registro de ventas:</b>\n\n';
ventasItems.forEach(item => {
  mensajeHTML += `<code>${item}</code>\n`;
});
mensajeHTML += `\n<b>üí∞ Total de la venta: $ ${totalFormateado}</b>`;

// Crear mensaje de texto plano (backup)
let mensajeTexto = '‚úÖ Registro de ventas:\n\n';
mensajeTexto += ventasItems.join('\n');
mensajeTexto += `\n\nüí∞ Total de la venta: $ ${totalFormateado}`;

// Crear objeto de respuesta
const resultado = {
  mensaje_html: mensajeHTML,
  mensaje_texto: mensajeTexto,
  parse_mode: 'HTML',
  total_venta: totalGeneral,
  cantidad_items: ventasItems.length,
  items: ventasItems,
  fecha_registro: new Date().toLocaleDateString('es-CO'),
  hora_registro: new Date().toLocaleTimeString('es-CO', {
    hour: '2-digit',
    minute: '2-digit',
    hour12: true
  })
};

// Retornar resultado en formato n8n
return [{ json: resultado }];

// ===== RESTAR VENTAS DEL INVENTARIO - VERSI√ìN CORREGIDA CON DEDUPLICACI√ìN =====
console.log('=== INICIO: ACTUALIZAR INVENTARIO CON VENTAS ===');

try {
  // 1. OBTENER VENTAS DESDE "Guardar_venta_en_hoja"
  console.log('1. Obteniendo ventas...');
  const ventasData = $('Guardar_venta_en_hoja').all();
  console.log('‚úÖ Ventas obtenidas:', ventasData?.length || 0, 'registros');

  // Debug: Mostrar estructura de ventas
  if (ventasData.length > 0) {
    console.log('üìã Estructura de venta:', JSON.stringify(ventasData[0].json, null, 2));
  }

  // 2. OBTENER INVENTARIO DESDE "Obtener_inventario_actual"  
  console.log('2. Obteniendo inventario actual...');
  const inventarioDataRaw = $('Obtener_inventario_actual').all();
  console.log('‚úÖ Inventario obtenido (raw):', inventarioDataRaw?.length || 0, 'registros');

  // ======= NUEVA SECCI√ìN: DEDUPLICAR INVENTARIO =======
  console.log('3. Deduplicando inventario...');
  const inventarioMap = new Map();

  inventarioDataRaw.forEach((item, index) => {
    const producto = item.json.PRODUCTO;
    const rowNumber = item.json.row_number;

    if (producto) {
      // Usar row_number como clave √∫nica, o producto si no hay row_number
      const key = rowNumber ? `${producto}_${rowNumber}` : producto;

      // Solo agregar si no existe o si este tiene row_number y el anterior no
      if (!inventarioMap.has(key)) {
        inventarioMap.set(key, item);
        console.log(`üìù Agregado: ${producto} (row: ${rowNumber})`);
      } else {
        console.log(`‚ö†Ô∏è Duplicado ignorado: ${producto} (row: ${rowNumber})`);
      }
    }
  });

  // Convertir el Map de vuelta a array
  const inventarioData = Array.from(inventarioMap.values());
  console.log('‚úÖ Inventario deduplicado:', inventarioData.length, 'registros √∫nicos');
  // ======= FIN DEDUPLICACI√ìN =======

  // 3. VALIDACIONES
  if (!ventasData || ventasData.length === 0) {
    console.log('‚ö†Ô∏è No hay ventas para procesar - RETORNANDO INVENTARIO SIN CAMBIOS');
    console.log('üîç PROBLEMA: El nodo de ventas no est√° devolviendo datos');
    console.log('üí° SOLUCI√ìN: Verificar que el nodo "Guardar_venta_en_hoja" se haya ejecutado correctamente');
    return inventarioData; // Devolver inventario sin cambios
  }

  if (!inventarioData || inventarioData.length === 0) {
    console.log('‚ùå No hay inventario para actualizar');
    return [];
  }

  // 4. CREAR MAPA DE INVENTARIO PARA B√öSQUEDA R√ÅPIDA (actualizado)
  console.log('4. Creando mapa de inventario para b√∫squedas...');
  const inventarioSearchMap = {};

  inventarioData.forEach((item, index) => {
    const producto = item.json.PRODUCTO;
    if (producto) {
      inventarioSearchMap[producto] = {
        data: { ...item.json }, // Copia de los datos
        index: index // √çndice original en el array deduplicado
      };
    }
  });

  console.log('üìã Productos √∫nicos en inventario:', Object.keys(inventarioSearchMap));

  // 5. SUMAR TODAS LAS VENTAS POR PRODUCTO (en caso de productos duplicados en ventas)
  console.log('5. Consolidando ventas por producto...');
  const ventasConsolidadas = {};

  ventasData.forEach((venta, ventaIndex) => {
    const productoVenta = venta.json.PRODUCTO;
    const cantidadVenta = parseInt(venta.json.CANTIDAD) || 0;

    console.log(`üîç Venta ${ventaIndex + 1}: ${productoVenta} - ${cantidadVenta} unidades`);

    if (!ventasConsolidadas[productoVenta]) {
      ventasConsolidadas[productoVenta] = 0;
    }
    ventasConsolidadas[productoVenta] += cantidadVenta;
  });

  console.log('üìã Ventas consolidadas:', ventasConsolidadas);

  // 6. APLICAR DESCUENTOS AL INVENTARIO
  console.log('6. Aplicando descuentos al inventario...');
  const inventarioActualizado = [];
  const productosActualizados = [];
  const productosNoEncontrados = [];

  // Procesar cada item del inventario deduplicado
  inventarioData.forEach((itemInventario, index) => {
    const productoInventario = itemInventario.json.PRODUCTO;
    let nuevoItem = { ...itemInventario.json }; // Copia del item original

    // Si este producto se vendi√≥, actualizar el stock
    if (ventasConsolidadas.hasOwnProperty(productoInventario)) {
      const cantidadTotalVendida = ventasConsolidadas[productoInventario];
      const stockActual = parseInt(itemInventario.json.STOCK_ACTUAL_UNIDADES) || 0;
      const nuevoStock = stockActual - cantidadTotalVendida;

      console.log(`üìä ${productoInventario}: ${stockActual} ‚Üí ${nuevoStock} (vendido: ${cantidadTotalVendida})`);

      // ACTUALIZAR EL STOCK_ACTUAL_UNIDADES
      nuevoItem.STOCK_ACTUAL_UNIDADES = nuevoStock;

      productosActualizados.push({
        producto: productoInventario,
        stockAnterior: stockActual,
        cantidadVendida: cantidadTotalVendida,
        stockNuevo: nuevoStock,
        rowNumber: nuevoItem.row_number
      });

      // Marcar como procesado
      delete ventasConsolidadas[productoInventario];
    }

    // Agregar el item (actualizado o no) al resultado
    inventarioActualizado.push({ json: nuevoItem });
  });

  // 7. IDENTIFICAR PRODUCTOS DE VENTAS NO ENCONTRADOS EN INVENTARIO
  Object.keys(ventasConsolidadas).forEach(producto => {
    productosNoEncontrados.push({
      producto: producto,
      cantidadVendida: ventasConsolidadas[producto]
    });
  });

  // 8. GENERAR REPORTE FINAL
  console.log(`\nüìä RESUMEN DE ACTUALIZACI√ìN:`);
  console.log(`   Total items inventario original: ${inventarioDataRaw.length}`);
  console.log(`   Total items inventario deduplicado: ${inventarioData.length}`);
  console.log(`   Productos actualizados: ${productosActualizados.length}`);
  console.log(`   Productos no encontrados: ${productosNoEncontrados.length}`);

  if (productosActualizados.length > 0) {
    console.log('\n‚úÖ PRODUCTOS ACTUALIZADOS:');
    productosActualizados.forEach(item => {
      console.log(`   - ${item.producto} (fila ${item.rowNumber}): ${item.stockAnterior} ‚Üí ${item.stockNuevo} (-${item.cantidadVendida})`);
    });
  }

  if (productosNoEncontrados.length > 0) {
    console.log('\n‚ùå PRODUCTOS NO ENCONTRADOS EN INVENTARIO:');
    productosNoEncontrados.forEach(item => {
      console.log(`   - ${item.producto}: ${item.cantidadVendida} unidades`);
    });
  }

  console.log('\nüì§ Retornando inventario actualizado...');

  // Debug: Mostrar primera actualizaci√≥n
  if (inventarioActualizado.length > 0) {
    console.log('üìã Primer item actualizado:', JSON.stringify(inventarioActualizado[0].json, null, 2));
  }

  console.log('=== FIN: ACTUALIZAR INVENTARIO CON VENTAS ===');

  // 9. RETORNAR INVENTARIO ACTUALIZADO EN EL FORMATO CORRECTO
  return inventarioActualizado;

} catch (error) {
  console.log('‚ùå ERROR GENERAL:', error.message);
  console.log('üìç Stack:', error.stack);
  return [{ 
    json: { 
      ERROR: 'Error procesando inventario', 
      detalle: error.message,
      stack: error.stack
    }
  }];
}
