// Capturamos el texto desde la propiedad correcta
const texto = String($input.first().json.output || '').trim();

// Regex para extraer "producto" cantidad
const regex = /"([^"]+)"\s+(\d+(?:\.\d+)?)/g;

const filas = [];
let match;

while ((match = regex.exec(texto)) !== null) {
  filas.push({
    PRODUCTO: match[1].replace(/_/g, ' '),
    CANTIDAD: parseFloat(match[2]),
    FECHA: new Date().toLocaleString('es-CO', { timeZone: 'America/Bogota' })
  });
}

return filas;

// Obtenemos todas las ventas desde Code_ventas
const ventas = $('Code_ventas').all().map(item => item.json);

// Obtenemos la lista de precios desde Obtener_lista_precios
const precios = $('Obtener_lista_precios').all().map(item => item.json);

// FunciÃ³n para buscar el precio de un producto
function buscarPrecio(nombreProducto) {
  const nombreVenta = nombreProducto.toLowerCase().replace(/_/g, ' ').trim();
  for (const p of precios) {
    const nombrePrecio = p.PRODUCTO.toLowerCase().replace(/_/g, ' ').trim();
    if (nombrePrecio === nombreVenta) {
      return parseFloat(p.PRECIO_UNITARIO);
    }
  }
  return 0; // Si no se encuentra, devuelve 0
}

// Procesamos todas las ventas
return ventas.map(v => {
  const precioUnitario = buscarPrecio(v.PRODUCTO);
  const valorTotal = Number(v.CANTIDAD) * precioUnitario;
  return {
    json: {
      PRODUCTO: v.PRODUCTO,
      CANTIDAD: Number(v.CANTIDAD),
      VALOR_UNITARIO: precioUnitario,
      VALOR_TOTAL: valorTotal,
      FECHA: v.FECHA
    }
  };
});

