// FunciÃ³n para normalizar fechas a formato DD-MM-YYYY
function normalizarFecha(fecha) {
  if (!fecha) return null;

  // Si ya estÃ¡ en formato DD-MM-YYYY
  if (typeof fecha === 'string' && fecha.match(/^\d{1,2}-\d{1,2}-\d{4}$/)) {
    const partes = fecha.split('-');
    const dia = partes[0];
    const mes = partes[1];
    const aÃ±o = partes[2];
    return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + aÃ±o;
  }

  // Si viene en formato "24/8/2025, 8:27:04 p. m."
  if (typeof fecha === 'string' && fecha.includes('/')) {
    try {
      const partesFecha = fecha.split(',')[0].trim();
      const partes = partesFecha.split('/');
      const dia = partes[0];
      const mes = partes[1];
      const aÃ±o = partes[2];
      return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + aÃ±o;
    } catch (error) {
      return null;
    }
  }

  return null;
}

// FunciÃ³n para formatear nÃºmeros con separadores de miles
function formatearPeso(valor) {
  return '$' + new Intl.NumberFormat('es-CO').format(valor);
}

// FunciÃ³n para obtener dÃ­a de la semana en espaÃ±ol
function obtenerDiaSemana(fecha) {
  const partes = fecha.split('-');
  const dia = partes[0];
  const mes = partes[1];
  const aÃ±o = partes[2];
  const fechaObj = new Date(aÃ±o, mes - 1, dia);
  const dias = ['Domingo', 'Lunes', 'Martes', 'MiÃ©rcoles', 'Jueves', 'Viernes', 'SÃ¡bado'];
  return dias[fechaObj.getDay()];
}

// FunciÃ³n para fecha legible corta
function fechaLegibleCorta(fecha) {
  const partes = fecha.split('-');
  const dia = partes[0];
  const mes = partes[1];
  return dia + '/' + mes;
}

// FunciÃ³n para convertir fecha a formato legible
function fechaLegible(fecha) {
  const partes = fecha.split('-');
  const dia = partes[0];
  const mes = partes[1];
  const aÃ±o = partes[2];
  const meses = ['enero', 'febrero', 'marzo', 'abril', 'mayo', 'junio',
                 'julio', 'agosto', 'septiembre', 'octubre', 'noviembre', 'diciembre'];
  const diaSemana = obtenerDiaSemana(fecha);
  const diaCapitalizado = diaSemana.charAt(0).toUpperCase() + diaSemana.slice(1);
  return diaCapitalizado + ', ' + parseInt(dia) + ' de ' + meses[parseInt(mes)-1] + ' de ' + aÃ±o;
}

// Obtener el tipo de consulta y fechas
const tipoConsulta = $('Interpretar_mensaje').item.json.queryType;
const fechaInicio = $('Interpretar_mensaje').item.json.fechaInicio;
const fechaFin = $('Interpretar_mensaje').item.json.fechaFin;
// Filtrar ventas por fecha (ahora maneja rangos)
const fechaInicioNormalizada = normalizarFecha(fechaInicio);
const fechaFinNormalizada = normalizarFecha(fechaFin);

console.log("Filtrando desde: " + fechaInicioNormalizada + " hasta: " + fechaFinNormalizada);

let ventasFiltradas = [];

for (let i = 0; i < todasLasVentas.length; i++) {
