  // Agrupar por productos (como antes)
  if (!resumenProductos[producto]) {
    resumenProductos[producto] = {
      cantidad: 0,
      valorTotal: 0
    };
  }

  resumenProductos[producto].cantidad += cantidad;
  resumenProductos[producto].valorTotal += valorTotal;

  // Nuevo: Agrupar por dÃ­a para reporte semanal
  if (!resumenPorDia[fechaVenta]) {
    resumenPorDia[fechaVenta] = 0;
  }
  resumenPorDia[fechaVenta] += valorTotal;

  valorTotalGeneral += valorTotal;
  cantidadTotalProductos += cantidad;
});

// Generar mensaje de WhatsApp segÃºn el tipo de consulta
let mensaje = '';

if (ventasFiltradas.length === 0) {
  const titulo = tipoConsulta === 'semana' ? 
    'ð Resumen de ventas semanal\nð ' + fechaLegibleCorta(fechaInicioNormalizada) + ' al ' + fechaLegibleCorta(fechaFinNormalizada) :
    'ð Resumen de ventas\nð ' + fechaLegible(fechaInicioNormalizada);

  mensaje = titulo + '\nNo se registraron ventas para este perÃ­odo.';

} else if (tipoConsulta === 'semana' || tipoConsulta === 'mes') {
  // FORMATO ESPECIAL PARA SEMANA/MES: Solo dÃ­as y totales
  mensaje = 'ð Resumen de ventas semanal\n';
  mensaje += 'ð ' + fechaLegibleCorta(fechaInicioNormalizada) + ' al ' + fechaLegibleCorta(fechaFinNormalizada) + '\n';

  // Ordenar dÃ­as por fecha
  const diasOrdenados = Object.keys(resumenPorDia).sort();

  diasOrdenados.forEach(function(fecha) {
    const diaSemana = obtenerDiaSemana(fecha);
    const total = resumenPorDia[fecha];
