  });

  mensaje += 'ð° Total general: ' + formatearPeso(valorTotalGeneral);

} else {
  // FORMATO NORMAL PARA DÃAS: Productos detallados
  mensaje = 'ð Resumen de ventas\n';
  mensaje += 'ð ' + fechaLegible(fechaInicioNormalizada) + '\n';

  // Ordenar productos por valor total (mayor a menor)
  const productosArray = Object.entries(resumenProductos);
  productosArray.sort(function(a, b) {
    return b[1].valorTotal - a[1].valorTotal;
  });

  // Lista de productos
  productosArray.forEach(function(item) {
    const producto = item[0];
    const datos = item[1];
    mensaje += 'â¢ ' + producto + ': ' + datos.cantidad + ' und â ' + formatearPeso(datos.valorTotal) + '\n';
  });

  // Total general
  mensaje += 'ð° Total general: ' + formatearPeso(valorTotalGeneral);
}

console.log("Ventas procesadas: " + ventasFiltradas.length);
console.log("Valor total: " + valorTotalGeneral);

// Retornar el mensaje
