// Obtener los datos del nodo de Google Sheets
const sheetData = $('crea_o_actualiza_productos').all();

// FunciÃ³n para generar la respuesta
function generateProductResponse(data) {
    if (!data || data.length === 0) {
        return {
            mensaje: "â No se pudo procesar ningÃºn producto. Por favor, verifica el formato del mensaje.",
            tipo: "error",
            productos_procesados: 0
        };
    }

    let productosValidos = [];
    let productosConError = [];

    // Procesar cada elemento de respuesta del sheets
    data.forEach((item, index) => {
        const json = item.json;

        // Verificar si hay error en la respuesta
        if (json.error || json.PRODUCTO === 'ERROR') {
            productosConError.push({
                error: json.error || 'Error desconocido',
                producto: json.PRODUCTO || 'Producto desconocido'
            });
            return;
        }

        const producto = {
            nombre: json.PRODUCTO || json.producto || json.name,
            precio: json.PRECIO_UNITARIO || json.precio || json.price
        };

        // Validar que tengamos datos vÃ¡lidos
        if (producto.nombre && producto.precio) {
            productosValidos.push(producto);
        }
    });

    // Generar mensaje de respuesta
    return buildResponseMessage(productosValidos, productosConError);
}

// FunciÃ³n para construir el mensaje de respuesta simplificado
function buildResponseMessage(productos, errores) {
    let mensaje = "";

    // Productos procesados exitosamente
    if (productos.length > 0) {
        mensaje += "â *Productos procesados:*\n\n";

        productos.forEach((producto, index) => {
            mensaje += `${index + 1}. ${producto.nombre} - $${formatPrice(producto.precio)}\n`;
        });
    }

    // Errores si los hay
    if (errores.length > 0) {
        if (productos.length > 0) mensaje += "\n";
        mensaje += "â ï¸ *Errores:*\n";
        errores.forEach((error, index) => {
            mensaje += `${index + 1}. ${error.producto}: ${error.error}\n`;
        });
    }

    // Si no hay productos procesados
    if (productos.length === 0 && errores.length === 0) {
        mensaje = "â No se encontraron productos vÃ¡lidos para procesar.\n\n";
        mensaje += "ð¡ *Formato correcto:*\n";
        mensaje += "â¢ producto a precio\n";
        mensaje += "â¢ Ejemplo: coca cola 350ml a 3000\n";
        mensaje += "â¢ Ejemplo: arepa burguer a 7000";
    }

    return {
        mensaje: mensaje.trim(),
        tipo: productos.length > 0 ? "success" : "error",
        productos_procesados: productos.length,
        errores: errores.length,
        detalles: {
            productos: productos,
            errores: errores
        }
    };
}

// FunciÃ³n para formatear precios (agregar separadores de miles)
function formatPrice(price) {
    return new Intl.NumberFormat('es-CO').format(price);
}

// FunciÃ³n alternativa si el nodo de sheets retorna datos en formato diferente
function generateResponseFromAlternativeFormat(data) {
    if (!data || data.length === 0) {
        return generateProductResponse([]);
    }

    // Si los datos vienen en formato diferente, adaptarlos
    const adaptedData = data.map(item => {
        return {
            json: {
                PRODUCTO: item.PRODUCTO || item.producto || item.name,
                PRECIO_UNITARIO: item.PRECIO_UNITARIO || item.precio || item.price,
                error: item.error
            }
        };
    });

    return generateProductResponse(adaptedData);
}

// Ejecutar la generaciÃ³n de respuesta
