// Normalizar texto mejorado (minÃºsculas, sin tildes, sin caracteres especiales)
function normalize(str) {
  return String(str)
    .toLowerCase()
    .trim()
    .replace(/[Ã¡Ã Ã¤Ã¢Ã£]/g, "a")
    .replace(/[Ã©Ã¨Ã«Ãª]/g, "e")
    .replace(/[Ã­Ã¬Ã¯Ã®]/g, "i")
    .replace(/[Ã³Ã²Ã¶Ã´Ãµ]/g, "o")
    .replace(/[ÃºÃ¹Ã¼Ã»]/g, "u")
