}

const msg = normalize(raw);
console.log("Mensaje normalizado:", msg);

// Obtener fecha de hoy en zona horaria de BogotÃ¡ optimizada
function todayInTZ(tz = "America/Bogota") {
  const now = new Date();
  const formatter = new Intl.DateTimeFormat("en-CA", {
    timeZone: tz,
    year: "numeric",
    month: "2-digit",
    day: "2-digit",
  });

  const [year, month, day] = formatter.format(now).split('-');
  return new Date(Number(year), Number(month) - 1, Number(day));
}

// FunciÃ³n para obtener el lunes de la semana actual
function getMondayOfWeek(date) {
  const d = new Date(date);
  const day = d.getDay(); // 0 = domingo, 1 = lunes, ..., 6 = sÃ¡bado
  const diff = day === 0 ? -6 : 1 - day; // Si es domingo, retroceder 6 dÃ­as
  d.setDate(d.getDate() + diff);
  return d;
}

// FunciÃ³n para obtener el domingo de la semana actual
function getSundayOfWeek(date) {
  const monday = getMondayOfWeek(date);
  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);
  return sunday;
}

function addDays(date, delta) {
  const result = new Date(date);
  result.setDate(result.getDate() + delta);
  return result;
}

function formatDDMMYYYY(date) {
  const dd = String(date.getDate()).padStart(2, "0");
  const mm = String(date.getMonth() + 1).padStart(2, "0");
  const yyyy = date.getFullYear();
  return `${dd}-${mm}-${yyyy}`;
}

// Patrones de consulta mÃ¡s robustos
const patterns = {
  hoy: /\b(hoy|dia de hoy|ventas de hoy|hoy ventas)\b/,
  ayer: /\b(ayer|dia de ayer|ventas de ayer|ayer ventas)\b/,
  semana: /\b(semana|esta semana|ventas de la semana|semana actual)\b/,
  semanaCompleta: /\b(semana completa|toda la semana|semana entera)\b/,
  mes: /\b(mes|este mes|ventas del mes|mes actual|ultimos 30 dias|30 dias)\b/,
  // Nuevos patrones
  antier: /\b(antier|anteayer|antes de ayer)\b/,
  ultimosDias: /\b(ultimos (\d+) dias|(\d+) dias)\b/
};

const hoy = todayInTZ();
let tipo = "dia";
let inicio = hoy;
let fin = hoy;
let descripcion = "Ventas de hoy";

// LÃ³gica de interpretaciÃ³n mejorada
if (patterns.ayer.test(msg)) {
  const ayer = addDays(hoy, -1);
  inicio = ayer;
  fin = ayer;
  tipo = "dia";
  descripcion = "Ventas de ayer";

} else if (patterns.antier.test(msg)) {
  const antier = addDays(hoy, -2);
  inicio = antier;
  fin = antier;
  tipo = "dia";
  descripcion = "Ventas de antier";

} else if (patterns.semana.test(msg)) {
  inicio = getMondayOfWeek(hoy); // Desde el lunes de esta semana
  fin = hoy; // Hasta hoy
  tipo = "semana";
  descripcion = "Ventas de esta semana (lunes a hoy)";

} else if (patterns.semanaCompleta.test(msg)) {
  inicio = getMondayOfWeek(hoy); // Desde el lunes
  fin = getSundayOfWeek(hoy); // Hasta el domingo (puede ser futuro)
  if (fin > hoy) fin = hoy; // No ir mÃ¡s allÃ¡ de hoy
  tipo = "semana";
  descripcion = "Ventas de la semana completa";

} else if (patterns.mes.test(msg)) {
  inicio = new Date(hoy.getFullYear(), hoy.getMonth(), 1);
  fin = hoy;
  tipo = "mes";
  descripcion = "Ventas del mes";

} else if (patterns.ultimosDias.test(msg)) {
  const match = msg.match(/(\d+)/);
  if (match) {
    const dias = parseInt(match[1]);
    if (dias > 0 && dias <= 365) { // Validar rango razonable
      inicio = addDays(hoy, -(dias - 1));
      fin = hoy;
      tipo = "rango";
      descripcion = `Ventas de los Ãºltimos ${dias} dÃ­as`;
    }
  }
} else if (patterns.hoy.test(msg)) {
  // ExplÃ­citamente "hoy" (ya estÃ¡ configurado por defecto)
  descripcion = "Ventas de hoy";
}

// Log de depuraciÃ³n
console.log(`InterpretaciÃ³n: ${descripcion}`);
console.log(`PerÃ­odo: ${formatDDMMYYYY(inicio)} al ${formatDDMMYYYY(fin)}`);

// ValidaciÃ³n de fechas
if (inicio > hoy) {
  console.log("Advertencia: Fecha de inicio en el futuro, ajustando a hoy");
  inicio = hoy;
}

if (fin > hoy) {
  console.log("Advertencia: Fecha de fin en el futuro, ajustando a hoy");
  fin = hoy;
}

// Resultado con mÃ¡s informaciÃ³n
return [{
  json: {
    // Datos originales (compatibilidad)
    queryType: tipo,
    fechaInicio: formatDDMMYYYY(inicio),
    fechaFin: formatDDMMYYYY(fin),
    textoInterpretado: msg,

    // Nuevos datos
    mensajeOriginal: raw,
    descripcion: descripcion,
    diasIncluidos: Math.ceil((fin - inicio) / (1000 * 60 * 60 * 24)) + 1,
    fechaConsulta: formatDDMMYYYY(hoy),

    // Para debugging
    debug: {
      patronEncontrado: Object.keys(patterns).find(key => patterns[key].test(msg)) || "hoy (default)",
      fechasCalculadas: {
        inicio: inicio.toISOString().split('T')[0],
        fin: fin.toISOString().split('T')[0]
      }
    }
  }
}];

// Obtener los datos del nodo de Google Sheets
const sheetData = $('crea_o_actualiza_productos').all();

// FunciÃ³n para generar la respuesta
function generateProductResponse(data) {
    if (!data || data.length === 0) {
        return {
            mensaje: "âŒ No se pudo procesar ningÃºn producto. Por favor, verifica el formato del mensaje.",
            tipo: "error",
            productos_procesados: 0
        };
    }

    let productosValidos = [];
    let productosConError = [];

    // Procesar cada elemento de respuesta del sheets
    data.forEach((item, index) => {
        const json = item.json;

        // Verificar si hay error en la respuesta
        if (json.error || json.PRODUCTO === 'ERROR') {
            productosConError.push({
                error: json.error || 'Error desconocido',
                producto: json.PRODUCTO || 'Producto desconocido'
            });
            return;
        }

        const producto = {
            nombre: json.PRODUCTO || json.producto || json.name,
            precio: json.PRECIO_UNITARIO || json.precio || json.price
        };

        // Validar que tengamos datos vÃ¡lidos
        if (producto.nombre && producto.precio) {
            productosValidos.push(producto);
        }
    });

    // Generar mensaje de respuesta
    return buildResponseMessage(productosValidos, productosConError);
}

// FunciÃ³n para construir el mensaje de respuesta simplificado
function buildResponseMessage(productos, errores) {
    let mensaje = "";

    // Productos procesados exitosamente
    if (productos.length > 0) {
        mensaje += "âœ… *Precio actualizado:*\n\n";

        productos.forEach((producto, index) => {
            mensaje += `${index + 1}. ${producto.nombre} - $${formatPrice(producto.precio)}\n`;
        });
    }

    // Errores si los hay
    if (errores.length > 0) {
        if (productos.length > 0) mensaje += "\n";
        mensaje += "âš ï¸ *Errores:*\n";
        errores.forEach((error, index) => {
            mensaje += `${index + 1}. ${error.producto}: ${error.error}\n`;
        });
    }

    // Si no hay productos procesados
    if (productos.length === 0 && errores.length === 0) {
        mensaje = "âŒ No se encontraron productos vÃ¡lidos para procesar.\n\n";
        mensaje += "ðŸ’¡ *Formato correcto:*\n";
        mensaje += "â€¢ producto a precio\n";
        mensaje += "â€¢ Ejemplo: coca cola 350ml a 3000\n";
        mensaje += "â€¢ Ejemplo: arepa burguer a 7000";
    }

    return {
        mensaje: mensaje.trim(),
        tipo: productos.length > 0 ? "success" : "error",
        productos_procesados: productos.length,
        errores: errores.length,
        detalles: {
            productos: productos,
            errores: errores
        }
    };
}

// FunciÃ³n para formatear precios (agregar separadores de miles)
function formatPrice(price) {
    return new Intl.NumberFormat('es-CO').format(price);
}

// FunciÃ³n alternativa si el nodo de sheets retorna datos en formato diferente
function generateResponseFromAlternativeFormat(data) {
    if (!data || data.length === 0) {
        return generateProductResponse([]);
    }

    // Si los datos vienen en formato diferente, adaptarlos
    const adaptedData = data.map(item => {
        return {
            json: {
                PRODUCTO: item.PRODUCTO || item.producto || item.name,
                PRECIO_UNITARIO: item.PRECIO_UNITARIO || item.precio || item.price,
                error: item.error
            }
        };
    });

    return generateProductResponse(adaptedData);
}

// Ejecutar la generaciÃ³n de respuesta
