let response;

// Intentar con el formato principal
try {
    response = generateProductResponse(sheetData);
} catch (error) {
    console.log('Error con formato principal, intentando formato alternativo:', error);

    // Si falla, intentar con formato alternativo
    try {
        response = generateResponseFromAlternativeFormat(sheetData);
    } catch (altError) {
        console.log('Error con formato alternativo:', altError);

        // Respuesta de error genÃ©rica
        response = {
