// FunciÃ³n para manejar plurales
const manejarPlural = (cantidad, producto) => {
  const cantidadNum = parseInt(cantidad);

  if (cantidadNum === 1) {
    const palabras = producto.trim().split(' ');
    let primeraPalabra = palabras[0];

    if (primeraPalabra.endsWith('s') && primeraPalabra.length > 1) {
      const excepcionesS = ['lapis', 'virus', 'tesis', 'crisis', 'oasis', 'martes', 'miercoles', 'jueves', 'viernes'];
      const palabraLower = primeraPalabra.toLowerCase();

      if (!excepcionesS.some(excepcion => palabraLower.includes(excepcion))) {
        if (primeraPalabra.endsWith('es') && primeraPalabra.length > 2) {
          const antesDe_es = primeraPalabra[primeraPalabra.length - 3];
          if (!'aeiouÃ¡Ã©Ã­Ã³Ãº'.includes(antesDe_es.toLowerCase())) {
            primeraPalabra = primeraPalabra.slice(0, -2);
          } else {
            primeraPalabra = primeraPalabra.slice(0, -1);
          }
        } else {
          primeraPalabra = primeraPalabra.slice(0, -1);
        }
      }
    }

    palabras[0] = primeraPalabra;
    return palabras.join(' ');
  }

  return producto;
};

