  // ======= NUEVA SECCIÃ“N: DEDUPLICAR INVENTARIO =======
  console.log('3. Deduplicando inventario...');
  const inventarioMap = new Map();

  inventarioDataRaw.forEach((item, index) => {
    const producto = item.json.PRODUCTO;
    const rowNumber = item.json.row_number;

    if (producto) {
      // Usar row_number como clave Ãºnica, o producto si no hay row_number
      const key = rowNumber ? `${producto}_${rowNumber}` : producto;

      // Solo agregar si no existe o si este tiene row_number y el anterior no
      if (!inventarioMap.has(key)) {
        inventarioMap.set(key, item);
        console.log(`ðŸ“ Agregado: ${producto} (row: ${rowNumber})`);
      } else {
        console.log(`âš ï¸ Duplicado ignorado: ${producto} (row: ${rowNumber})`);
      }
    }
  });

  // Convertir el Map de vuelta a array
  const inventarioData = Array.from(inventarioMap.values());
  console.log('âœ… Inventario deduplicado:', inventarioData.length, 'registros Ãºnicos');
  // ======= FIN DEDUPLICACIÃ“N =======

  // 3. VALIDACIONES
