    return inventarioData; // Devolver inventario sin cambios
  }

  if (!inventarioData || inventarioData.length === 0) {
    console.log('âŒ No hay inventario para actualizar');
    return [];
  }

  // 4. CREAR MAPA DE INVENTARIO PARA BÃšSQUEDA RÃPIDA (actualizado)
  console.log('4. Creando mapa de inventario para bÃºsquedas...');
  const inventarioSearchMap = {};

  inventarioData.forEach((item, index) => {
    const producto = item.json.PRODUCTO;
