        index: index // Ãndice original en el array deduplicado
      };
    }
  });

  console.log('ðŸ“‹ Productos Ãºnicos en inventario:', Object.keys(inventarioSearchMap));

  // 5. SUMAR TODAS LAS VENTAS POR PRODUCTO (en caso de productos duplicados en ventas)
  console.log('5. Consolidando ventas por producto...');
  const ventasConsolidadas = {};

  ventasData.forEach((venta, ventaIndex) => {
    const productoVenta = venta.json.PRODUCTO;
    const cantidadVenta = parseInt(venta.json.CANTIDAD) || 0;

    console.log(`ðŸ” Venta ${ventaIndex + 1}: ${productoVenta} - ${cantidadVenta} unidades`);

    if (!ventasConsolidadas[productoVenta]) {
      ventasConsolidadas[productoVenta] = 0;
    }
    ventasConsolidadas[productoVenta] += cantidadVenta;
  });

  console.log('ðŸ“‹ Ventas consolidadas:', ventasConsolidadas);

  // 6. APLICAR DESCUENTOS AL INVENTARIO
  console.log('6. Aplicando descuentos al inventario...');
  const inventarioActualizado = [];
  const productosActualizados = [];
  const productosNoEncontrados = [];

  // Procesar cada item del inventario deduplicado
  inventarioData.forEach((itemInventario, index) => {
    const productoInventario = itemInventario.json.PRODUCTO;
    let nuevoItem = { ...itemInventario.json }; // Copia del item original

    // Si este producto se vendiÃ³, actualizar el stock
    if (ventasConsolidadas.hasOwnProperty(productoInventario)) {
      const cantidadTotalVendida = ventasConsolidadas[productoInventario];
      const stockActual = parseInt(itemInventario.json.STOCK_ACTUAL_UNIDADES) || 0;
