// Acceder a TODOS los items del nodo de consumos
let consumosPersonales = [];

// Usar $items para obtener todos los items del nodo especÃ­fico
try {
  const todosLosConsumos = $items("ingresa_consumos_personales");
  if (todosLosConsumos && Array.isArray(todosLosConsumos)) {
    consumosPersonales = todosLosConsumos.map(item => item.json);
  }
} catch (error) {
  // MÃ©todo fallback si $items no funciona
  const consumosDirectos = $node["ingresa_consumos_personales"];
  if (consumosDirectos && consumosDirectos.json) {
    consumosPersonales = [consumosDirectos.json];
  }
}

// Procesar inventario (extraer datos json)
const inventarioData = $input.all();
const inventarioItems = [];

inventarioData.forEach(item => {
  if (item.json && item.json.STOCK_ACTUAL_UNIDADES !== undefined) {
    inventarioItems.push(item.json);
  }
});

// Verificar que tenemos datos vÃ¡lidos - mejorar el manejo de errores
if (consumosPersonales.length === 0) {
  // Si no hay consumos, no hacer nada y retornar vacÃ­o (no error)
  return [];
}

if (inventarioItems.length === 0) {
  // Si no hay inventario aÃºn, no hacer nada y retornar vacÃ­o (no error)
  return [];
}

// Eliminar duplicados del inventario basÃ¡ndose en PRODUCTO y row_number
const inventarioSinDuplicados = inventarioItems.filter((item, index, self) => 
  index === self.findIndex(i => i.PRODUCTO === item.PRODUCTO && i.row_number === item.row_number)
);

// Crear un mapa de consumos por producto
const consumosPorProducto = {};
consumosPersonales.forEach(consumo => {
  const producto = consumo.PRODUCTO;
  const cantidad = consumo.CANTIDAD;

  if (consumosPorProducto[producto]) {
    consumosPorProducto[producto] += cantidad;
  } else {
    consumosPorProducto[producto] = cantidad;
  }
});

// Procesar el inventario y descontar los consumos
const inventarioActualizado = inventarioSinDuplicados.map(item => {
  const producto = item.PRODUCTO;
  const stockActual = item.STOCK_ACTUAL_UNIDADES;

  // Verificar si este producto fue consumido
  if (consumosPorProducto[producto]) {
    const cantidadConsumida = consumosPorProducto[producto];
    const nuevoStock = Math.max(0, stockActual - cantidadConsumida);

    return {
      ...item,
      STOCK_ACTUAL_UNIDADES: nuevoStock
    };
  }

  // Si no fue consumido, mantener el stock actual
  return item;
});

// Retornar solo el inventario actualizado
return inventarioActualizado;

