// Obtener fecha de hoy en zona horaria de Bogot√° optimizada
function todayInTZ(tz = "America/Bogota") {
  const now = new Date();
  const formatter = new Intl.DateTimeFormat("en-CA", {
    timeZone: tz,
    year: "numeric",
    month: "2-digit", 
    day: "2-digit",
  });

  const [year, month, day] = formatter.format(now).split('-');
  return new Date(Number(year), Number(month) - 1, Number(day));
}

// Funci√≥n para obtener el lunes de la semana actual
function getMondayOfWeek(date) {
  const d = new Date(date);
  const day = d.getDay(); // 0 = domingo, 1 = lunes, ..., 6 = s√°bado
  const diff = day === 0 ? -6 : 1 - day; // Si es domingo, retroceder 6 d√≠as
  d.setDate(d.getDate() + diff);
  return d;
}

// Funci√≥n para obtener el domingo de la semana actual
function getSundayOfWeek(date) {
  const monday = getMondayOfWeek(date);
  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);
  return sunday;
}

function addDays(date, delta) {
  const result = new Date(date);
  result.setDate(result.getDate() + delta);
  return result;
}

function formatDDMMYYYY(date) {
  const dd = String(date.getDate()).padStart(2, "0");
  const mm = String(date.getMonth() + 1).padStart(2, "0");
  const yyyy = date.getFullYear();
  return `${dd}-${mm}-${yyyy}`;
}

// Patrones de consulta m√°s robustos
const patterns = {
  hoy: /\b(hoy|dia de hoy|ventas de hoy|hoy ventas)\b/,
  ayer: /\b(ayer|dia de ayer|ventas de ayer|ayer ventas)\b/,
  semana: /\b(semana|esta semana|ventas de la semana|semana actual)\b/,
  semanaCompleta: /\b(semana completa|toda la semana|semana entera)\b/,
  mes: /\b(mes|este mes|ventas del mes|mes actual|ultimos 30 dias|30 dias)\b/,
  // Nuevos patrones
  antier: /\b(antier|anteayer|antes de ayer)\b/,
  ultimosDias: /\b(ultimos (\d+) dias|(\d+) dias)\b/
};

const hoy = todayInTZ();
let tipo = "dia";
let inicio = hoy;
let fin = hoy;
let descripcion = "Ventas de hoy";

// L√≥gica de interpretaci√≥n mejorada
if (patterns.ayer.test(msg)) {
  const ayer = addDays(hoy, -1);
  inicio = ayer;
  fin = ayer;
  tipo = "dia";
  descripcion = "Ventas de ayer";

} else if (patterns.antier.test(msg)) {
  const antier = addDays(hoy, -2);
  inicio = antier;
  fin = antier;
  tipo = "dia";
  descripcion = "Ventas de antier";

} else if (patterns.semana.test(msg)) {
  inicio = getMondayOfWeek(hoy); // Desde el lunes de esta semana
  fin = hoy; // Hasta hoy
  tipo = "semana";
  descripcion = "Ventas de esta semana (lunes a hoy)";

} else if (patterns.semanaCompleta.test(msg)) {
  inicio = getMondayOfWeek(hoy); // Desde el lunes
  fin = getSundayOfWeek(hoy); // Hasta el domingo (puede ser futuro)
  if (fin > hoy) fin = hoy; // No ir m√°s all√° de hoy
  tipo = "semana";
  descripcion = "Ventas de la semana completa";

} else if (patterns.mes.test(msg)) {
  inicio = new Date(hoy.getFullYear(), hoy.getMonth(), 1);
  fin = hoy;
  tipo = "mes";
  descripcion = "Ventas del mes";

} else if (patterns.ultimosDias.test(msg)) {
  const match = msg.match(/(\d+)/);
  if (match) {
    const dias = parseInt(match[1]);
    if (dias > 0 && dias <= 365) { // Validar rango razonable
      inicio = addDays(hoy, -(dias - 1));
      fin = hoy;
      tipo = "rango";
      descripcion = `Ventas de los √∫ltimos ${dias} d√≠as`;
    }
  }
} else if (patterns.hoy.test(msg)) {
  // Expl√≠citamente "hoy" (ya est√° configurado por defecto)
  descripcion = "Ventas de hoy";
}

// Log de depuraci√≥n
