// Generador de mensaje WhatsApp para resumen de ventas - Versi√≥n corregida
const fechaObjetivo = $('Interpretar_mensaje').item.json.fechaInicio;
const todasLasVentas = $('ventas_solicitadas').all();

console.log("Generando resumen para fecha: " + fechaObjetivo);

// Funci√≥n para normalizar fechas a formato DD-MM-YYYY
function normalizarFecha(fecha) {
  if (!fecha) return null;

  // Si ya est√° en formato DD-MM-YYYY
  if (typeof fecha === 'string' && fecha.match(/^\d{1,2}-\d{1,2}-\d{4}$/)) {
    const partes = fecha.split('-');
    const dia = partes[0];
    const mes = partes[1];
    const a√±o = partes[2];
    return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + a√±o;
  }

  // Si viene en formato "24/8/2025, 8:27:04 p. m."
  if (typeof fecha === 'string' && fecha.includes('/')) {
    try {
      const partesFecha = fecha.split(',')[0].trim();
      const partes = partesFecha.split('/');
      const dia = partes[0];
      const mes = partes[1];
      const a√±o = partes[2];
      return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + a√±o;
    } catch (error) {
      return null;
    }
  }

  return null;
}

// Funci√≥n para formatear n√∫meros con separadores de miles
function formatearPeso(valor) {
  return '$' + new Intl.NumberFormat('es-CO').format(valor);
}

// Funci√≥n para obtener d√≠a de la semana en espa√±ol
function obtenerDiaSemana(fecha) {
  const partes = fecha.split('-');
  const dia = partes[0];
  const mes = partes[1];
  const a√±o = partes[2];
  const fechaObj = new Date(a√±o, mes - 1, dia);
  const dias = ['Domingo', 'Lunes', 'Martes', 'Mi√©rcoles', 'Jueves', 'Viernes', 'S√°bado'];
  return dias[fechaObj.getDay()];
}

// Funci√≥n para fecha legible corta
function fechaLegibleCorta(fecha) {
  const partes = fecha.split('-');
  const dia = partes[0];
  const mes = partes[1];
  return dia + '/' + mes;
}

// Funci√≥n para convertir fecha a formato legible
function fechaLegible(fecha) {
  const partes = fecha.split('-');
  const dia = partes[0];
  const mes = partes[1];
  const a√±o = partes[2];
  const meses = ['enero', 'febrero', 'marzo', 'abril', 'mayo', 'junio',
                 'julio', 'agosto', 'septiembre', 'octubre', 'noviembre', 'diciembre'];
  const diaSemana = obtenerDiaSemana(fecha);
  const diaCapitalizado = diaSemana.charAt(0).toUpperCase() + diaSemana.slice(1);
  return diaCapitalizado + ', ' + parseInt(dia) + ' de ' + meses[parseInt(mes)-1] + ' de ' + a√±o;
}

// Obtener el tipo de consulta y fechas
const tipoConsulta = $('Interpretar_mensaje').item.json.queryType;
const fechaInicio = $('Interpretar_mensaje').item.json.fechaInicio;
const fechaFin = $('Interpretar_mensaje').item.json.fechaFin;

// Filtrar ventas por fecha (ahora maneja rangos)
const fechaInicioNormalizada = normalizarFecha(fechaInicio);
let fechaFinNormalizada = normalizarFecha(fechaFin);

// Si fechaFin es null, usar fechaInicio como fecha fin (consulta de un solo d√≠a)
if (!fechaFinNormalizada) {
  fechaFinNormalizada = fechaInicioNormalizada;
}

console.log("Filtrando desde: " + fechaInicioNormalizada + " hasta: " + fechaFinNormalizada);

let ventasFiltradas = [];

for (let i = 0; i < todasLasVentas.length; i++) {
  const venta = todasLasVentas[i].json;
  const fechaVenta = venta.FECHA;

  if (fechaVenta) {
    const fechaVentaNormalizada = normalizarFecha(fechaVenta);

    // Comparar si la fecha est√° en el rango
    if (fechaVentaNormalizada >= fechaInicioNormalizada && fechaVentaNormalizada <= fechaFinNormalizada) {
      ventasFiltradas.push(venta);
    }
  }
}

// Procesar datos para el resumen
let resumenProductos = {};
let resumenPorDia = {}; // Nuevo: para agrupar por d√≠a
let valorTotalGeneral = 0;
let cantidadTotalProductos = 0;

ventasFiltradas.forEach(function(venta) {
  const producto = venta.PRODUCTO;
  const cantidad = parseInt(venta.CANTIDAD) || 0;
  const valorTotal = parseFloat(venta.VALOR_TOTAL) || 0;
  const fechaVenta = normalizarFecha(venta.FECHA);

  // Agrupar por productos (como antes)
  if (!resumenProductos[producto]) {
    resumenProductos[producto] = {
      cantidad: 0,
      valorTotal: 0
    };
  }

  resumenProductos[producto].cantidad += cantidad;
  resumenProductos[producto].valorTotal += valorTotal;

  // Nuevo: Agrupar por d√≠a para reporte semanal
  if (!resumenPorDia[fechaVenta]) {
    resumenPorDia[fechaVenta] = 0;
  }
  resumenPorDia[fechaVenta] += valorTotal;

  valorTotalGeneral += valorTotal;
  cantidadTotalProductos += cantidad;
});

// Generar mensaje de WhatsApp seg√∫n el tipo de consulta
let mensaje = '';

if (ventasFiltradas.length === 0) {
  let titulo;
  if (tipoConsulta === 'semana') {
    titulo = 'üìä Resumen de ventas semanal\nüìÖ ' + fechaLegibleCorta(fechaInicioNormalizada) + ' al ' + fechaLegibleCorta(fechaFinNormalizada);
  } else {
    titulo = 'üìä Resumen de ventas\nüìÖ ' + fechaLegible(fechaInicioNormalizada);
  }
  mensaje = titulo + '\nNo se registraron ventas para este per√≠odo.';

} else if (tipoConsulta === 'semana' || tipoConsulta === 'mes') {
  // FORMATO ESPECIAL PARA SEMANA/MES: Solo d√≠as y totales
  mensaje = 'üìä Resumen de ventas semanal\n';
  mensaje += 'üìÖ ' + fechaLegibleCorta(fechaInicioNormalizada) + ' al ' + fechaLegibleCorta(fechaFinNormalizada) + '\n';

  // Ordenar d√≠as por fecha
  const diasOrdenados = Object.keys(resumenPorDia).sort();

  diasOrdenados.forEach(function(fecha) {
    const diaSemana = obtenerDiaSemana(fecha);
    const total = resumenPorDia[fecha];
    mensaje += '‚Ä¢ ' + diaSemana + ': ' + formatearPeso(total) + '\n';
  });

  mensaje += 'üí∞ Total general: ' + formatearPeso(valorTotalGeneral);

} else {
  // FORMATO NORMAL PARA D√çAS: Productos detallados
  mensaje = 'üìä Resumen de ventas\n';
  mensaje += 'üìÖ ' + fechaLegible(fechaInicioNormalizada) + '\n';

  // Ordenar productos por valor total (mayor a menor)
  const productosArray = Object.entries(resumenProductos);
  productosArray.sort(function(a, b) {
    return b[1].valorTotal - a[1].valorTotal;
  });

  // Lista de productos
  productosArray.forEach(function(item) {
    const producto = item[0];
    const datos = item[1];
    mensaje += '‚Ä¢ ' + producto + ': ' + datos.cantidad + ' und ‚Äî ' + formatearPeso(datos.valorTotal) + '\n';
  });

  // Total general
  mensaje += 'üí∞ Total general: ' + formatearPeso(valorTotalGeneral);
}

console.log("Ventas procesadas: " + ventasFiltradas.length);
console.log("Valor total: " + valorTotalGeneral);

// Retornar el mensaje
return [
  {
    json: {
      mensaje_telegram: mensaje,
      tipo_consulta: tipoConsulta,
      fecha_inicio: fechaInicioNormalizada,
      fecha_fin: fechaFinNormalizada,
      total_ventas: valorTotalGeneral,
      total_productos: cantidadTotalProductos,
      total_transacciones: ventasFiltradas.length,
      resumen_productos: resumenProductos,
      resumen_por_dia: resumenPorDia // Nuevo campo
    }
  }
];

// Obtener los datos del nodo de Google Sheets
const sheetData = $('crea_o_actualiza_productos').all();

// Funci√≥n para generar la respuesta
function generateProductResponse(data) {
    if (!data || data.length === 0) {
        return {
            mensaje: "‚ùå No se pudo procesar ning√∫n producto. Por favor, verifica el formato del mensaje.",
            tipo: "error",
            productos_procesados: 0
        };
    }

    let productosCreados = [];
    let productosActualizados = [];
    let productosConError = [];

    // Procesar cada elemento de respuesta del sheets
    data.forEach((item, index) => {
        const json = item.json;

        // Verificar si hay error en la respuesta
        if (json.error || json.PRODUCTO === 'ERROR') {
            productosConError.push({
                error: json.error || 'Error desconocido',
                producto: json.PRODUCTO || 'Producto desconocido'
            });
            return;
        }

        // Verificar si el producto fue creado o actualizado
        // Esto depende de c√≥mo est√© configurado tu nodo de sheets
        // Asumiendo que tienes campos que indican el tipo de operaci√≥n

        const producto = {
            nombre: json.PRODUCTO || json.producto || json.name,
            precio: json.PRECIO_UNITARIO || json.precio || json.price,
            // Detectar si fue actualizaci√≥n (puedes ajustar esta l√≥gica seg√∫n tu sheets)
            esActualizacion: json.updated || json.isUpdate || false
        };

        // Validar que tengamos datos v√°lidos
        if (producto.nombre && producto.precio) {
            if (producto.esActualizacion) {
                productosActualizados.push(producto);
            } else {
                productosCreados.push(producto);
            }
        }
    });

    // Generar mensaje de respuesta
    return buildResponseMessage(productosCreados, productosActualizados, productosConError);
}

// Funci√≥n para construir el mensaje de respuesta
function buildResponseMessage(creados, actualizados, errores) {
    let mensaje = "";
    let totalProcesados = creados.length + actualizados.length;

    // Encabezado principal
    if (totalProcesados > 0) {
        mensaje += "‚úÖ *Productos procesados exitosamente*\n\n";
    }

    // Productos creados
    if (creados.length > 0) {
        mensaje += "üÜï *PRODUCTOS CREADOS:*\n";
        creados.forEach((producto, index) => {
            mensaje += `${index + 1}. ${producto.nombre} - $${formatPrice(producto.precio)}\n`;
        });
        mensaje += "\n";
    }

    // Productos actualizados
    if (actualizados.length > 0) {
        mensaje += "üîÑ *PRODUCTOS ACTUALIZADOS:*\n";
        actualizados.forEach((producto, index) => {
            mensaje += `${index + 1}. ${producto.nombre} - $${formatPrice(producto.precio)}\n`;
        });
        mensaje += "\n";
    }

    // Resumen
    if (totalProcesados > 0) {
        mensaje += `üìä *Resumen:*\n`;
        if (creados.length > 0) mensaje += `‚Ä¢ ${creados.length} producto(s) creado(s)\n`;
        if (actualizados.length > 0) mensaje += `‚Ä¢ ${actualizados.length} producto(s) actualizado(s)\n`;
        mensaje += `‚Ä¢ Total procesados: ${totalProcesados}\n`;
    }

    // Errores si los hay
    if (errores.length > 0) {
        mensaje += "\n‚ö†Ô∏è *ERRORES:*\n";
        errores.forEach((error, index) => {
            mensaje += `${index + 1}. ${error.producto}: ${error.error}\n`;
        });
    }

    // Si no hay productos procesados
    if (totalProcesados === 0 && errores.length === 0) {
        mensaje = "‚ùå No se encontraron productos v√°lidos para procesar.\n\n";
        mensaje += "üí° *Formato correcto:*\n";
        mensaje += "‚Ä¢ producto a precio\n";
        mensaje += "‚Ä¢ Ejemplo: coca cola 350ml a 3000\n";
        mensaje += "‚Ä¢ Ejemplo: arepa burguer a 7000";
    }

    return {
        mensaje: mensaje.trim(),
        tipo: totalProcesados > 0 ? "success" : "error",
        productos_procesados: totalProcesados,
        productos_creados: creados.length,
        productos_actualizados: actualizados.length,
        errores: errores.length,
        detalles: {
            creados: creados,
            actualizados: actualizados,
            errores: errores
        }
    };
}

// Funci√≥n para formatear precios (agregar separadores de miles)
function formatPrice(price) {
    return new Intl.NumberFormat('es-CO').format(price);
}

// Funci√≥n alternativa si el nodo de sheets retorna datos en formato diferente
function generateResponseFromAlternativeFormat(data) {
    if (!data || data.length === 0) {
        return generateProductResponse([]);
    }

    // Si los datos vienen en formato diferente, adaptarlos
    const adaptedData = data.map(item => {
        return {
            json: {
                PRODUCTO: item.PRODUCTO || item.producto || item.name,
                PRECIO_UNITARIO: item.PRECIO_UNITARIO || item.precio || item.price,
                updated: item.updated || item.isUpdate || false,
                error: item.error
            }
        };
    });

    return generateProductResponse(adaptedData);
}

