// Obtener los datos de entrada
const inputData = $input.all();

// Funci√≥n para parsear el output y convertirlo al formato requerido
function parseOutputToSheetFormat(data) {
    const results = [];

    // Procesar cada item de entrada
    data.forEach((item, index) => {
        try {
            // Obtener el texto del output
            const outputText = item.json.output;

            if (!outputText) {
                console.log(`Item ${index}: No se encontr√≥ campo 'output'`);
                return;
            }

            // Llama a la nueva funci√≥n que maneja m√∫ltiples productos
            const parsedItems = parseMultipleProductData(outputText);

            if (parsedItems.length > 0) {
                // Agregar cada producto individual a los resultados
                parsedItems.forEach(product => {
                    results.push({
                        PRODUCTO: product.PRODUCTO,
                        PRECIO_UNITARIO: product.PRECIO_UNITARIO,
                        item_index: index,
                        original_output: outputText
                    });
                });
            } else {
                console.log(`Item ${index}: No se pudieron extraer datos v√°lidos`);
                // Agregar item con error para tracking
                results.push({
                    PRODUCTO: 'ERROR_PARSING',
                    PRECIO_UNITARIO: 0,
                    error: 'No se pudieron extraer producto y precio',
                    original_output: outputText,
                    item_index: index
                });
            }

        } catch (error) {
            console.log(`Error procesando item ${index}:`, error.message);
            results.push({
                PRODUCTO: 'ERROR',
                PRECIO_UNITARIO: 0,
                error: error.message,
                item_index: index
            });
        }
    });

    return results;
}

// ----> FUNCI√ìN MODIFICADA <----
// Ahora esta funci√≥n procesa m√∫ltiples productos en una sola cadena de texto
function parseMultipleProductData(outputText) {
    const allProducts = [];
    // Dividir el texto en bloques de productos, cada uno separado por dos saltos de l√≠nea
    const productBlocks = outputText.split('\n\n');

    productBlocks.forEach(block => {
        const lines = block.split('\n');

        // Asume que el primer elemento es el producto y el segundo el precio
        if (lines.length >= 2) {
            const productNameMatch = lines[0].match(/PRODUCTO:\s*(.*)/i);
            const productPriceMatch = lines[1].match(/PRECIO_UNITARIO:\s*(\d+)/i);

            if (productNameMatch && productPriceMatch) {
                const productName = productNameMatch[1].trim();
                const productPrice = parseInt(productPriceMatch[1], 10);

                if (productName && !isNaN(productPrice)) {
                    allProducts.push({
                        PRODUCTO: productName,
                        PRECIO_UNITARIO: productPrice
                    });
                }
            }
        }
    });

    return allProducts;
}

