// Tomar el mensaje desde Telegram Trigger con manejo de errores
let raw = "";
try {
  raw = $input.first().json.mensaje || "";
} catch (error) {
  console.log("Error obteniendo mensaje de Telegram:", error.message);
  return [{ json: { error: "No se pudo obtener el mensaje de Telegram" } }];
}

// Normalizar texto mejorado (min√∫sculas, sin tildes, sin caracteres especiales)
function normalize(str) {
  return String(str)
    .toLowerCase()
    .trim()
    .replace(/[√°√†√§√¢√£]/g, "a")
    .replace(/[√©√®√´√™]/g, "e")
    .replace(/[√≠√¨√Ø√Æ]/g, "i")
    .replace(/[√≥√≤√∂√¥√µ]/g, "o")
    .replace(/[√∫√π√º√ª]/g, "u")
    .replace(/[√±]/g, "n")
    .replace(/[√ß]/g, "c")
    .replace(/\s+/g, " "); // M√∫ltiples espacios a uno solo
}

const msg = normalize(raw);
console.log("Mensaje normalizado:", msg);

// Obtener fecha de hoy en zona horaria de Bogot√° optimizada
function todayInTZ(tz = "America/Bogota") {
  const now = new Date();
  const formatter = new Intl.DateTimeFormat("en-CA", {
    timeZone: tz,
    year: "numeric",
    month: "2-digit",
    day: "2-digit",
  });

  const [year, month, day] = formatter.format(now).split('-');
  return new Date(Number(year), Number(month) - 1, Number(day));
}

// Funci√≥n para obtener el lunes de la semana actual
function getMondayOfWeek(date) {
  const d = new Date(date);
  const day = d.getDay(); // 0 = domingo, 1 = lunes, ..., 6 = s√°bado
  const diff = day === 0 ? -6 : 1 - day; // Si es domingo, retroceder 6 d√≠as
  d.setDate(d.getDate() + diff);
  return d;
}

// Funci√≥n para obtener el domingo de la semana actual
function getSundayOfWeek(date) {
  const monday = getMondayOfWeek(date);
  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);
  return sunday;
}

function addDays(date, delta) {
  const result = new Date(date);
  result.setDate(result.getDate() + delta);
  return result;
}

function formatDDMMYYYY(date) {
  const dd = String(date.getDate()).padStart(2, "0");
  const mm = String(date.getMonth() + 1).padStart(2, "0");
  const yyyy = date.getFullYear();
  return `${dd}-${mm}-${yyyy}`;
}

// Patrones de consulta m√°s robustos
const patterns = {
  hoy: /\b(hoy|dia de hoy|ventas de hoy|hoy ventas)\b/,
  ayer: /\b(ayer|dia de ayer|ventas de ayer|ayer ventas)\b/,
  semana: /\b(semana|esta semana|ventas de la semana|semana actual)\b/,
  semanaCompleta: /\b(semana completa|toda la semana|semana entera)\b/,
  mes: /\b(mes|este mes|ventas del mes|mes actual|ultimos 30 dias|30 dias)\b/,
  // Nuevos patrones
  antier: /\b(antier|anteayer|antes de ayer)\b/,
  ultimosDias: /\b(ultimos (\d+) dias|(\d+) dias)\b/
};

const hoy = todayInTZ();
let tipo = "dia";
let inicio = hoy;
let fin = hoy;
let descripcion = "Ventas de hoy";

// L√≥gica de interpretaci√≥n mejorada
if (patterns.ayer.test(msg)) {
  const ayer = addDays(hoy, -1);
  inicio = ayer;
  fin = ayer;
  tipo = "dia";
  descripcion = "Ventas de ayer";

} else if (patterns.antier.test(msg)) {
  const antier = addDays(hoy, -2);
  inicio = antier;
  fin = antier;
  tipo = "dia";
  descripcion = "Ventas de antier";

} else if (patterns.semana.test(msg)) {
  inicio = getMondayOfWeek(hoy); // Desde el lunes de esta semana
  fin = hoy; // Hasta hoy
  tipo = "semana";
  descripcion = "Ventas de esta semana (lunes a hoy)";

} else if (patterns.semanaCompleta.test(msg)) {
  inicio = getMondayOfWeek(hoy); // Desde el lunes
  fin = getSundayOfWeek(hoy); // Hasta el domingo (puede ser futuro)
  if (fin > hoy) fin = hoy; // No ir m√°s all√° de hoy
  tipo = "semana";
  descripcion = "Ventas de la semana completa";

} else if (patterns.mes.test(msg)) {
  inicio = new Date(hoy.getFullYear(), hoy.getMonth(), 1);
  fin = hoy;
  tipo = "mes";
  descripcion = "Ventas del mes";

} else if (patterns.ultimosDias.test(msg)) {
  const match = msg.match(/(\d+)/);
  if (match) {
    const dias = parseInt(match[1]);
    if (dias > 0 && dias <= 365) { // Validar rango razonable
      inicio = addDays(hoy, -(dias - 1));
      fin = hoy;
      tipo = "rango";
      descripcion = `Ventas de los √∫ltimos ${dias} d√≠as`;
    }
  }
} else if (patterns.hoy.test(msg)) {
  // Expl√≠citamente "hoy" (ya est√° configurado por defecto)
  descripcion = "Ventas de hoy";
}

// Log de depuraci√≥n
console.log(`Interpretaci√≥n: ${descripcion}`);
console.log(`Per√≠odo: ${formatDDMMYYYY(inicio)} al ${formatDDMMYYYY(fin)}`);

// Validaci√≥n de fechas
if (inicio > hoy) {
  console.log("Advertencia: Fecha de inicio en el futuro, ajustando a hoy");
  inicio = hoy;
}

if (fin > hoy) {
  console.log("Advertencia: Fecha de fin en el futuro, ajustando a hoy");
  fin = hoy;
}

// Resultado con m√°s informaci√≥n
return [{
  json: {
    // Datos originales (compatibilidad)
    queryType: tipo,
    fechaInicio: formatDDMMYYYY(inicio),
    fechaFin: formatDDMMYYYY(fin),
    textoInterpretado: msg,

    // Nuevos datos
    mensajeOriginal: raw,
    descripcion: descripcion,
    diasIncluidos: Math.ceil((fin - inicio) / (1000 * 60 * 60 * 24)) + 1,
    fechaConsulta: formatDDMMYYYY(hoy),

    // Para debugging
    debug: {
      patronEncontrado: Object.keys(patterns).find(key => patterns[key].test(msg)) || "hoy (default)",
      fechasCalculadas: {
        inicio: inicio.toISOString().split('T')[0],
        fin: fin.toISOString().split('T')[0]
      }
    }
  }
}];

// ===== GENERADOR DE MENSAJE DE VENTAS =====
console.log('=== GENERANDO MENSAJE DE CONFIRMACI√ìN ===');

try {
  // Obtener datos del nodo anterior
  const datos = $input.first().json;

  // ===== FUNCIONES AUXILIARES =====

  function formatearNumero(valor) {
    return new Intl.NumberFormat('es-CO').format(valor);
  }

  function formatearPeso(valor) {
    return '$' + formatearNumero(valor);
  }

  function manejarSingularPlural(cantidad, palabra) {
    if (cantidad === 1) {
      if (palabra === 'unidades') {
        return 'unidad';
      }
      if (palabra.endsWith('s')) {
        return palabra.slice(0, -1);
      }
    }
    return palabra;
  }

  function obtenerDiaSemana(fecha) {
    const [dia, mes, a√±o] = fecha.split('-');
    const fechaObj = new Date(a√±o, mes - 1, dia);
    const dias = ['Domingo', 'Lunes', 'Martes', 'Mi√©rcoles', 'Jueves', 'Viernes', 'S√°bado'];
    return dias[fechaObj.getDay()];
  }

  function fechaLegible(fecha) {
    const [dia, mes, a√±o] = fecha.split('-');
    const meses = ['enero', 'febrero', 'marzo', 'abril', 'mayo', 'junio',
                   'julio', 'agosto', 'septiembre', 'octubre', 'noviembre', 'diciembre'];
    const diaSemana = obtenerDiaSemana(fecha);
    return `${diaSemana}, ${parseInt(dia)} de ${meses[parseInt(mes)-1]} de ${a√±o}`;
  }

  function fechaLegibleCorta(fecha) {
    const [dia, mes] = fecha.split('-');
    return `${dia}/${mes}`;
  }

  // ===== CONSTRUIR MENSAJE =====
  let mensaje = '';

  if (datos.tipoConsulta === 'semana' || datos.tipoConsulta === 'mes') {
    mensaje = 'üìä RESUMEN SEMANAL\n';
    mensaje += `üìÖ ${fechaLegibleCorta(datos.fechaInicio)} al ${fechaLegibleCorta(datos.fechaFin)}\n`;
  } else {
    mensaje = 'üìä RESUMEN DE VENTAS\n';
    mensaje += `üìÖ ${fechaLegible(datos.fechaInicio)}\n`;
  }

  mensaje += '‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n';

  // ===== SECCI√ìN DE VENTAS =====
  if (datos.hay_ventas) {
    mensaje += 'üì¶ *PRODUCTOS VENDIDOS:*\n';

    // Lista de productos que no muestran stock y tienen orden fijo
    const productosSinStock = ["Arepas de QUESO", "Arepa BURGUER", "Chorizos"];

    const productosVentas = Object.entries(datos.ventas.productos);

    // Separar excluidos y normales
    const excluidos = productosSinStock
      .map(nombre => productosVentas.find(([producto]) => producto === nombre))
      .filter(Boolean); // elimina los que no existan en ventas

    const normales = productosVentas
      .filter(([producto]) => !productosSinStock.includes(producto))
      .sort(([,a], [,b]) => b.cantidad - a.cantidad);

    const productosOrdenados = [...excluidos, ...normales];

    productosOrdenados.forEach(([producto, info]) => {
      mensaje += `‚Ä¢ ${formatearNumero(info.cantidad)} ${producto} | ${formatearPeso(info.valor)}`;

      if (!productosSinStock.includes(producto)) {
        if (info.stock > 0) {
          const stockTexto = manejarSingularPlural(info.stock, 'unidades');
          mensaje += ` | Stock: ${formatearNumero(info.stock)} ${stockTexto}\n`;
        } else if (info.stock === 0) {
          mensaje += ' | ‚ö†Ô∏è Agotado\n';
        } else {
          mensaje += '\n';
        }
      } else {
        mensaje += '\n';
      }
    });

    mensaje += '\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n';
    mensaje += `üìà TOTAL VENTAS: ${formatearPeso(datos.ventas.total)}\n\n`;
  } else {
    mensaje += '‚ö†Ô∏è No se registraron ventas para este per√≠odo\n\n';
  }

  // ===== SECCI√ìN DE COMPRAS =====
  if (datos.hay_compras) {
    mensaje += 'üõí *COMPRAS:*\n';
    const productosCompras = Object.entries(datos.compras.productos)
      .sort(([,a], [,b]) => b.cantidad - a.cantidad);

    productosCompras.forEach(([producto, info]) => {
      const factorTexto = manejarSingularPlural(info.cantidad, info.factor);
      mensaje += `‚Ä¢ ${formatearNumero(info.cantidad)} ${factorTexto} ${producto}\n`;
    });
    mensaje += '\n';
  }

  // ===== SECCI√ìN DE CONSUMOS PERSONALES =====
  if (datos.hay_consumos) {
    mensaje += 'üè† *CONSUMOS PERSONALES:*\n';
    const productosConsumos = Object.entries(datos.consumos.productos)
      .sort(([,a], [,b]) => b - a);

    productosConsumos.forEach(([producto, cantidad]) => {
      mensaje += `‚Ä¢ ${formatearNumero(cantidad)} ${producto}\n`;
    });
  }

  console.log('‚úÖ Mensaje generado exitosamente');
  console.log(`   ‚Ä¢ Longitud: ${mensaje.length} caracteres`);
  console.log(`   ‚Ä¢ Productos vendidos: ${datos.ventas.cantidad_productos}`);
  console.log(`   ‚Ä¢ Total ventas: ${formatearPeso(datos.ventas.total)}`);

  return [{
    json: {
      mensaje_texto: mensaje,
      estadisticas: {
        total_ventas: datos.ventas.total,
        productos_vendidos: datos.ventas.cantidad_productos,
        productos_consumidos: datos.consumos.cantidad_productos,
        productos_comprados: datos.compras.cantidad_productos,
        hay_ventas: datos.hay_ventas,
        hay_consumos: datos.hay_consumos,
        hay_compras: datos.hay_compras
      }
    }
  }];

} catch (error) {
  console.log('‚ùå ERROR generando mensaje:', error.message);

  return [{
    json: {
      error: true,
      mensaje_texto: `‚ùå Error generando mensaje: ${error.message}`,
      timestamp: new Date().toISOString()
    }
  }];
}

// ===== EXTRACTOR SIMPLE DE DATOS DE VENTAS =====
console.log('=== EXTRAYENDO DATOS PARA MENSAJE DE CONFIRMACI√ìN ===');

try {
  // ===== OBTENER FECHAS DEL MENSAJE INTERPRETADO =====
  const consulta = $('Interpretar_mensaje').item.json;
  const fechaInicio = consulta.fechaInicio;
  const fechaFin = consulta.fechaFin;
  const tipoConsulta = consulta.queryType;

  console.log(`üìÖ Consultando desde: ${fechaInicio} hasta: ${fechaFin} (${tipoConsulta})`);

  // ===== FUNCI√ìN PARA NORMALIZAR FECHAS =====
  function normalizarFecha(fecha) {
    if (!fecha) return null;

    // DD-MM-YYYY -> mantener
    if (fecha.match(/^\d{1,2}-\d{1,2}-\d{4}$/)) {
      const [dia, mes, a√±o] = fecha.split('-');
      return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + a√±o;
    }

    // DD/MM/YYYY -> DD-MM-YYYY
    if (fecha.includes('/')) {
      const [dia, mes, a√±o] = fecha.split(',')[0].split('/');
      return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + a√±o;
    }

    return null;
  }

  // ===== FILTRAR DATOS POR FECHA =====
  function filtrarPorFecha(datos, fechaField) {
    const fechaInicioNorm = normalizarFecha(fechaInicio);
    const fechaFinNorm = normalizarFecha(fechaFin);

    return datos.filter(item => {
      const fechaItem = normalizarFecha(item[fechaField]);
      return fechaItem >= fechaInicioNorm && fechaItem <= fechaFinNorm;
    });
  }

  // ===== EXTRAER VENTAS =====
  const ventasRaw = $('ventas_solicitadas').all().map(item => item.json);
  const ventasFiltradas = filtrarPorFecha(ventasRaw, 'FECHA');

  // Resumir ventas por producto
  const ventasResumen = {};
  let totalVentas = 0;

  ventasFiltradas.forEach(venta => {
    const producto = venta.PRODUCTO;
    const cantidad = parseInt(venta.CANTIDAD) || 0;
    const valorTotal = parseFloat(venta.VALOR_TOTAL) || 0;

    if (!ventasResumen[producto]) {
      ventasResumen[producto] = { cantidad: 0, valor: 0 };
    }

    ventasResumen[producto].cantidad += cantidad;
    ventasResumen[producto].valor += valorTotal;
    totalVentas += valorTotal;
  });

  // ===== EXTRAER INVENTARIO =====
  const inventarioRaw = $('obtener_inventario').all().map(item => item.json);
  const inventario = {};

  inventarioRaw.forEach(item => {
    const producto = item.PRODUCTO || item.producto;
    const stock = parseInt(item.STOCK_ACTUAL_UNIDADES || item.stock_actual_unidades) || 0;

    if (producto && producto.trim()) {
      inventario[producto.trim()] = stock;
    }
  });

  // ===== EXTRAER CONSUMOS =====
  const consumosRaw = $('obtener_consumos').all().map(item => item.json);
  const consumosFiltrados = filtrarPorFecha(consumosRaw, 'FECHA');

  const consumosResumen = {};
  let totalConsumos = 0;

  consumosFiltrados.forEach(consumo => {
    const producto = consumo.PRODUCTO;
    const cantidad = parseInt(consumo.CANTIDAD) || 0;

    if (!consumosResumen[producto]) {
      consumosResumen[producto] = 0;
    }

    consumosResumen[producto] += cantidad;
    totalConsumos += cantidad;
  });

  // ===== EXTRAER COMPRAS =====
  const comprasRaw = $('registroEntradaInventario').all().map(item => item.json);
  const comprasFiltradas = filtrarPorFecha(comprasRaw, 'FECHA');

  const comprasResumen = {};
  let totalCompras = 0;

  comprasFiltradas.forEach(compra => {
    const producto = compra.PRODUCTO;
    const cantidad = parseInt(compra.CANTIDAD) || 0;
    const factor = compra.FACTOR_UNIDADES || 'unidades';

    if (!comprasResumen[producto]) {
      comprasResumen[producto] = { cantidad: 0, factor };
    }

    comprasResumen[producto].cantidad += cantidad;
    totalCompras += cantidad;
  });

  // ===== BUSCAR STOCK DE PRODUCTOS VENDIDOS =====
  function buscarStock(nombreProducto) {
    if (!nombreProducto) return 0;

    const nombre = nombreProducto.trim().toLowerCase();

    // B√∫squeda exacta
    for (const [producto, stock] of Object.entries(inventario)) {
      if (producto.toLowerCase() === nombre) {
        return stock;
      }
    }

    // B√∫squeda parcial
    for (const [producto, stock] of Object.entries(inventario)) {
      if (producto.toLowerCase().includes(nombre) || nombre.includes(producto.toLowerCase())) {
        return stock;
      }
    }

    return 0;
  }

  // Agregar stock a productos vendidos
  Object.keys(ventasResumen).forEach(producto => {
    ventasResumen[producto].stock = buscarStock(producto);
  });

  // ===== DATOS FINALES SIMPLIFICADOS =====
  const datosExtraidos = {
    // Metadatos de consulta
    fechaInicio: normalizarFecha(fechaInicio),
    fechaFin: normalizarFecha(fechaFin),
    tipoConsulta: tipoConsulta,

    // Datos principales
    ventas: {
      productos: ventasResumen,
      total: totalVentas,
      cantidad_productos: Object.keys(ventasResumen).length,
      total_transacciones: ventasFiltradas.length
    },

    consumos: {
      productos: consumosResumen,
      total_cantidad: totalConsumos,
      cantidad_productos: Object.keys(consumosResumen).length
    },

    compras: {
      productos: comprasResumen,
      total_cantidad: totalCompras,
      cantidad_productos: Object.keys(comprasResumen).length
    },

    inventario: {
      productos: inventario,
      total_productos: Object.keys(inventario).length
    },

    // Flags √∫tiles para el mensaje
    hay_ventas: Object.keys(ventasResumen).length > 0,
    hay_consumos: Object.keys(consumosResumen).length > 0,
    hay_compras: Object.keys(comprasResumen).length > 0,
    hay_productos_agotados: Object.values(ventasResumen).some(v => v.stock === 0)
  };

  console.log('‚úÖ Datos extra√≠dos exitosamente:');
  console.log(`   ‚Ä¢ Ventas: ${datosExtraidos.ventas.cantidad_productos} productos, $${datosExtraidos.ventas.total.toLocaleString()}`);
  console.log(`   ‚Ä¢ Consumos: ${datosExtraidos.consumos.cantidad_productos} productos, ${datosExtraidos.consumos.total_cantidad} unidades`);
  console.log(`   ‚Ä¢ Compras: ${datosExtraidos.compras.cantidad_productos} productos, ${datosExtraidos.compras.total_cantidad} items`);
  console.log(`   ‚Ä¢ Inventario: ${datosExtraidos.inventario.total_productos} productos`);

  return [{
    json: datosExtraidos
  }];

} catch (error) {
  console.log('‚ùå ERROR:', error.message);

  return [{
    json: {
      error: true,
      mensaje: `Error extrayendo datos: ${error.message}`,
      timestamp: new Date().toISOString()
    }
  }];
}
