// Tomar el mensaje desde Telegram Trigger con manejo de errores
let raw = "";
try {
  raw = $input.first().json.mensaje || "";
} catch (error) {
  console.log("Error obteniendo mensaje de Telegram:", error.message);
  return [{ json: { error: "No se pudo obtener el mensaje de Telegram" } }];
}

// Normalizar texto mejorado (min√∫sculas, sin tildes, sin caracteres especiales)
function normalize(str) {
  return String(str)
    .toLowerCase()
    .trim()
    .replace(/[√°√†√§√¢√£]/g, "a")
    .replace(/[√©√®√´√™]/g, "e")
    .replace(/[√≠√¨√Ø√Æ]/g, "i")
    .replace(/[√≥√≤√∂√¥√µ]/g, "o")
    .replace(/[√∫√π√º√ª]/g, "u")
    .replace(/[√±]/g, "n")
    .replace(/[√ß]/g, "c")
    .replace(/\s+/g, " "); // M√∫ltiples espacios a uno solo
}

const msg = normalize(raw);
console.log("Mensaje normalizado:", msg);

// Obtener fecha de hoy en zona horaria de Bogot√° optimizada
function todayInTZ(tz = "America/Bogota") {
  const now = new Date();
  const formatter = new Intl.DateTimeFormat("en-CA", {
    timeZone: tz,
    year: "numeric",
    month: "2-digit",
    day: "2-digit",
  });

  const [year, month, day] = formatter.format(now).split('-');
  return new Date(Number(year), Number(month) - 1, Number(day));
}

// Funci√≥n para obtener el lunes de la semana actual
function getMondayOfWeek(date) {
  const d = new Date(date);
  const day = d.getDay(); // 0 = domingo, 1 = lunes, ..., 6 = s√°bado
  const diff = day === 0 ? -6 : 1 - day; // Si es domingo, retroceder 6 d√≠as
  d.setDate(d.getDate() + diff);
  return d;
}

// Funci√≥n para obtener el domingo de la semana actual
function getSundayOfWeek(date) {
  const monday = getMondayOfWeek(date);
  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);
  return sunday;
}

function addDays(date, delta) {
  const result = new Date(date);
  result.setDate(result.getDate() + delta);
  return result;
}

function formatDDMMYYYY(date) {
  const dd = String(date.getDate()).padStart(2, "0");
  const mm = String(date.getMonth() + 1).padStart(2, "0");
  const yyyy = date.getFullYear();
  return `${dd}-${mm}-${yyyy}`;
}

// Patrones de consulta m√°s robustos
const patterns = {
  hoy: /\b(hoy|dia de hoy|ventas de hoy|hoy ventas)\b/,
  ayer: /\b(ayer|dia de ayer|ventas de ayer|ayer ventas)\b/,
  semana: /\b(semana|esta semana|ventas de la semana|semana actual)\b/,
  semanaCompleta: /\b(semana completa|toda la semana|semana entera)\b/,
  mes: /\b(mes|este mes|ventas del mes|mes actual|ultimos 30 dias|30 dias)\b/,
  // Nuevos patrones
  antier: /\b(antier|anteayer|antes de ayer)\b/,
  ultimosDias: /\b(ultimos (\d+) dias|(\d+) dias)\b/
};

const hoy = todayInTZ();
let tipo = "dia";
let inicio = hoy;
let fin = hoy;
let descripcion = "Ventas de hoy";

// L√≥gica de interpretaci√≥n mejorada
if (patterns.ayer.test(msg)) {
  const ayer = addDays(hoy, -1);
  inicio = ayer;
  fin = ayer;
  tipo = "dia";
  descripcion = "Ventas de ayer";

} else if (patterns.antier.test(msg)) {
  const antier = addDays(hoy, -2);
  inicio = antier;
  fin = antier;
  tipo = "dia";
  descripcion = "Ventas de antier";

} else if (patterns.semana.test(msg)) {
  inicio = getMondayOfWeek(hoy); // Desde el lunes de esta semana
  fin = hoy; // Hasta hoy
  tipo = "semana";
  descripcion = "Ventas de esta semana (lunes a hoy)";

} else if (patterns.semanaCompleta.test(msg)) {
  inicio = getMondayOfWeek(hoy); // Desde el lunes
  fin = getSundayOfWeek(hoy); // Hasta el domingo (puede ser futuro)
  if (fin > hoy) fin = hoy; // No ir m√°s all√° de hoy
  tipo = "semana";
  descripcion = "Ventas de la semana completa";

} else if (patterns.mes.test(msg)) {
  inicio = new Date(hoy.getFullYear(), hoy.getMonth(), 1);
  fin = hoy;
  tipo = "mes";
  descripcion = "Ventas del mes";

} else if (patterns.ultimosDias.test(msg)) {
  const match = msg.match(/(\d+)/);
  if (match) {
    const dias = parseInt(match[1]);
    if (dias > 0 && dias <= 365) { // Validar rango razonable
      inicio = addDays(hoy, -(dias - 1));
      fin = hoy;
      tipo = "rango";
      descripcion = `Ventas de los √∫ltimos ${dias} d√≠as`;
    }
  }
} else if (patterns.hoy.test(msg)) {
  // Expl√≠citamente "hoy" (ya est√° configurado por defecto)
  descripcion = "Ventas de hoy";
}

// Log de depuraci√≥n
console.log(`Interpretaci√≥n: ${descripcion}`);
console.log(`Per√≠odo: ${formatDDMMYYYY(inicio)} al ${formatDDMMYYYY(fin)}`);

// Validaci√≥n de fechas
if (inicio > hoy) {
  console.log("Advertencia: Fecha de inicio en el futuro, ajustando a hoy");
  inicio = hoy;
}

if (fin > hoy) {
  console.log("Advertencia: Fecha de fin en el futuro, ajustando a hoy");
  fin = hoy;
}

// Resultado con m√°s informaci√≥n
return [{
  json: {
    // Datos originales (compatibilidad)
    queryType: tipo,
    fechaInicio: formatDDMMYYYY(inicio),
    fechaFin: formatDDMMYYYY(fin),
    textoInterpretado: msg,

    // Nuevos datos
    mensajeOriginal: raw,
    descripcion: descripcion,
    diasIncluidos: Math.ceil((fin - inicio) / (1000 * 60 * 60 * 24)) + 1,
    fechaConsulta: formatDDMMYYYY(hoy),

    // Para debugging
    debug: {
      patronEncontrado: Object.keys(patterns).find(key => patterns[key].test(msg)) || "hoy (default)",
      fechasCalculadas: {
        inicio: inicio.toISOString().split('T')[0],
        fin: fin.toISOString().split('T')[0]
      }
    }
  }
}];

// ===== MENSAJE MEJORADO CON UNIDADES VENDIDAS, STOCK ACTUAL Y CONSUMOS DEL DUE√ëO =====
console.log('=== GENERANDO REPORTE DE VENTAS CON INVENTARIO Y CONSUMOS ===');

try {
  // Obtener datos de ventas
  const fechaObjetivo = $('Interpretar_mensaje').item.json.fechaInicio;
  const todasLasVentas = $('ventas_solicitadas').all();

  console.log("Generando resumen para fecha: " + fechaObjetivo);
  console.log("Total ventas obtenidas:", todasLasVentas.length);

  // ===== OBTENER INVENTARIO ACTUAL (SIN DUPLICADOS) =====
  let inventarioActual = {};
  try {
    console.log("Obteniendo inventario desde nodo 'obtener_inventario' (ya sin duplicados)...");

    // Obtener datos desde el nodo Remove Duplicates
    const inventarioData = $('obtener_inventario').all();
    console.log("Datos de inventario √∫nicos obtenidos:", inventarioData.length, "registros");

    // Procesar cada registro de inventario (ahora sin duplicados)
    inventarioData.forEach((item, index) => {
      try {
        // Extraer datos del item
        const registro = item.json || item;

        const producto = registro.PRODUCTO || registro.producto;
        const stockRaw = registro.STOCK_ACTUAL_UNIDADES || registro.stock_actual_unidades;
        const stock = parseInt(stockRaw) || 0;

        if (producto && producto.trim() !== '') {
          inventarioActual[producto.trim()] = stock;
          console.log(`‚úÖ Inventario: ${producto} = ${stock} unidades`);
        } else {
          console.log(`‚ö†Ô∏è Registro ${index} sin nombre de producto v√°lido:`, registro);
        }
      } catch (itemError) {
        console.log(`‚ùå Error procesando registro ${index}:`, itemError.message);
      }
    });

    console.log("üìä Inventario procesado exitosamente:");
    console.log(`   ‚Ä¢ Productos √∫nicos: ${Object.keys(inventarioActual).length}`);
    console.log(`   ‚Ä¢ Productos con stock > 0: ${Object.values(inventarioActual).filter(stock => stock > 0).length}`);
    console.log(`   ‚Ä¢ Productos sin stock: ${Object.values(inventarioActual).filter(stock => stock === 0).length}`);

  } catch (error) {
    console.log("‚ùå Error obteniendo inventario desde nodo:", error.message);
    console.log("üîÑ Continuando sin datos de inventario...");
  }

  // ===== OBTENER CONSUMOS DEL DUE√ëO =====
  let todosLosConsumos = [];
  try {
    console.log("Obteniendo consumos desde nodo 'obtener_consumos'...");

    const consumosData = $('obtener_consumos').all();
    console.log("Datos de consumos obtenidos:", consumosData.length, "registros");

    consumosData.forEach((item, index) => {
      try {
        const registro = item.json || item;

        const producto = registro.PRODUCTO;
        const cantidad = parseInt(registro.CANTIDAD) || 0;
        const fecha = registro.FECHA;

        if (producto && producto.trim() !== '' && cantidad > 0 && fecha) {
          todosLosConsumos.push({
            PRODUCTO: producto.trim(),
            CANTIDAD: cantidad,
            FECHA: fecha
          });
          console.log(`‚úÖ Consumo: ${producto} = ${cantidad} unidades en ${fecha}`);
        } else {
          console.log(`‚ö†Ô∏è Registro ${index} de consumo inv√°lido:`, registro);
        }
      } catch (itemError) {
        console.log(`‚ùå Error procesando consumo ${index}:`, itemError.message);
      }
    });

    console.log("üìä Consumos procesados exitosamente:");
    console.log(`   ‚Ä¢ Total registros de consumos: ${todosLosConsumos.length}`);

  } catch (error) {
    console.log("‚ùå Error obteniendo consumos desde nodo:", error.message);
    console.log("üîÑ Continuando sin datos de consumos...");
  }

  // ===== FUNCIONES AUXILIARES =====

  // Funci√≥n para normalizar fechas a formato DD-MM-YYYY
  function normalizarFecha(fecha) {
    if (!fecha) return null;

    // Si ya est√° en formato DD-MM-YYYY
    if (typeof fecha === 'string' && fecha.match(/^\d{1,2}-\d{1,2}-\d{4}$/)) {
      const partes = fecha.split('-');
      const dia = partes[0];
      const mes = partes[1];
      const a√±o = partes[2];
      return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + a√±o;
    }

    // Si viene en formato "24/8/2025, 8:27:04 p. m."
    if (typeof fecha === 'string' && fecha.includes('/')) {
      try {
        const partesFecha = fecha.split(',')[0].trim();
        const partes = partesFecha.split('/');
        const dia = partes[0];
        const mes = partes[1];
        const a√±o = partes[2];
        return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + a√±o;
      } catch (error) {
        return null;
      }
    }

    return null;
  }

  // Funci√≥n para formatear n√∫meros con separadores de miles
  function formatearNumero(valor) {
    return new Intl.NumberFormat('es-CO').format(valor);
  }

  function formatearPeso(valor) {
    return '$' + formatearNumero(valor);
  }

  // Funci√≥n para obtener d√≠a de la semana en espa√±ol
  function obtenerDiaSemana(fecha) {
    const partes = fecha.split('-');
    const dia = partes[0];
    const mes = partes[1];
    const a√±o = partes[2];
    const fechaObj = new Date(a√±o, mes - 1, dia);
    const dias = ['Domingo', 'Lunes', 'Martes', 'Mi√©rcoles', 'Jueves', 'Viernes', 'S√°bado'];
    return dias[fechaObj.getDay()];
  }

  // Funci√≥n para fecha legible corta
  function fechaLegibleCorta(fecha) {
    const partes = fecha.split('-');
    const dia = partes[0];
    const mes = partes[1];
    return dia + '/' + mes;
  }

  // Funci√≥n para convertir fecha a formato legible
  function fechaLegible(fecha) {
    const partes = fecha.split('-');
    const dia = partes[0];
    const mes = partes[1];
    const a√±o = partes[2];
    const meses = ['enero', 'febrero', 'marzo', 'abril', 'mayo', 'junio',
                   'julio', 'agosto', 'septiembre', 'octubre', 'noviembre', 'diciembre'];
    const diaSemana = obtenerDiaSemana(fecha);
    const diaCapitalizado = diaSemana.charAt(0).toUpperCase() + diaSemana.slice(1);
    return diaCapitalizado + ', ' + parseInt(dia) + ' de ' + meses[parseInt(mes)-1] + ' de ' + a√±o;
  }

  // ===== PROCESAMIENTO DE VENTAS =====

  // Obtener el tipo de consulta y fechas
  const tipoConsulta = $('Interpretar_mensaje').item.json.queryType;
  const fechaInicio = $('Interpretar_mensaje').item.json.fechaInicio;
  const fechaFin = $('Interpretar_mensaje').item.json.fechaFin;

  // Filtrar ventas por fecha
  const fechaInicioNormalizada = normalizarFecha(fechaInicio);
  const fechaFinNormalizada = normalizarFecha(fechaFin);

  console.log("üóìÔ∏è Filtrando ventas desde: " + fechaInicioNormalizada + " hasta: " + fechaFinNormalizada);

  let ventasFiltradas = [];

  for (let i = 0; i < todasLasVentas.length; i++) {
    const venta = todasLasVentas[i].json;
    const fechaVenta = venta.FECHA;

    if (fechaVenta) {
      const fechaVentaNormalizada = normalizarFecha(fechaVenta);

      // Comparar si la fecha est√° en el rango
      if (fechaVentaNormalizada >= fechaInicioNormalizada && fechaVentaNormalizada <= fechaFinNormalizada) {
        ventasFiltradas.push(venta);
      }
    }
  }

  console.log("üìä Ventas filtradas:", ventasFiltradas.length);

  // ===== FILTRAR CONSUMOS POR FECHA =====
  let consumosFiltrados = [];

  for (let i = 0; i < todosLosConsumos.length; i++) {
    const consumo = todosLosConsumos[i];
    const fechaConsumo = consumo.FECHA;

    if (fechaConsumo) {
      const fechaConsumoNormalizada = normalizarFecha(fechaConsumo);

      // Comparar si la fecha est√° en el rango
      if (fechaConsumoNormalizada >= fechaInicioNormalizada && fechaConsumoNormalizada <= fechaFinNormalizada) {
        consumosFiltrados.push(consumo);
      }
    }
  }

  console.log("üìä Consumos filtrados:", consumosFiltrados.length);

  // ===== FUNCI√ìN MEJORADA PARA BUSCAR STOCK =====
  function buscarStockProducto(nombreProducto) {
    if (!nombreProducto) return 0;

    const nombreLimpio = nombreProducto.trim().toLowerCase();

    // 1. B√∫squeda exacta (m√°s eficiente)
    for (const [producto, stock] of Object.entries(inventarioActual)) {
      if (producto.toLowerCase() === nombreLimpio) {
        console.log(`üéØ Coincidencia exacta: "${nombreProducto}" = ${stock} unidades`);
        return stock;
      }
    }

    // 2. B√∫squeda por coincidencia parcial
    for (const [producto, stock] of Object.entries(inventarioActual)) {
      const productoLimpio = producto.toLowerCase();

      // Buscar coincidencias de palabras
      const palabrasInventario = productoLimpio.split(' ');
      const palabrasVenta = nombreLimpio.split(' ');

      const coincidencias = palabrasInventario.filter(palabra => 
        palabrasVenta.some(palabraVenta => 
          palabraVenta.includes(palabra) || palabra.includes(palabraVenta)
        )
      );

      if (coincidencias.length > 0) {
        console.log(`üîç Coincidencia parcial: "${nombreProducto}" -> "${producto}" (${stock} unidades)`);
        return stock;
      }
    }

    console.log(`‚ùì Sin coincidencia de stock para: "${nombreProducto}"`);
    return 0; // No se encontr√≥ coincidencia
  }

  // ===== PROCESAR DATOS CON INVENTARIO =====
  let resumenProductos = {};
  let resumenPorDia = {}; 
  let valorTotalGeneral = 0;
  let costoTotalGeneral = 0;
  let utilidadTotalGeneral = 0;
  let cantidadTotalProductos = 0;

  ventasFiltradas.forEach(function(venta) {
    const producto = venta.PRODUCTO;
    const cantidad = parseInt(venta.CANTIDAD) || 0;
    const valorUnitario = parseFloat(venta.VALOR_UNITARIO) || 0;
    const costo = parseFloat(venta.COSTO) || 0;
    const valorTotal = parseFloat(venta.VALOR_TOTAL) || 0;
    const fechaVenta = normalizarFecha(venta.FECHA);

    // Calcular costo total y utilidad total del item
    const costoTotal = cantidad * costo;
    const utilidadTotal = cantidad * (valorUnitario - costo);

    // Agrupar por productos
    if (!resumenProductos[producto]) {
      const stockActual = buscarStockProducto(producto);

      resumenProductos[producto] = {
        cantidad: 0,
        valorTotal: 0,
        costoTotal: 0,
        utilidadTotal: 0,
        stockActual: stockActual
      };
    }

    resumenProductos[producto].cantidad += cantidad;
    resumenProductos[producto].valorTotal += valorTotal;
    resumenProductos[producto].costoTotal += costoTotal;
    resumenProductos[producto].utilidadTotal += utilidadTotal;

    // Agrupar por d√≠a para reporte semanal
    if (!resumenPorDia[fechaVenta]) {
      resumenPorDia[fechaVenta] = {
        valorTotal: 0,
        costoTotal: 0,
        utilidadTotal: 0
      };
    }
    resumenPorDia[fechaVenta].valorTotal += valorTotal;
    resumenPorDia[fechaVenta].costoTotal += costoTotal;
    resumenPorDia[fechaVenta].utilidadTotal += utilidadTotal;

    valorTotalGeneral += valorTotal;
    costoTotalGeneral += costoTotal;
    utilidadTotalGeneral += utilidadTotal;
    cantidadTotalProductos += cantidad;
  });

  // ===== PROCESAR CONSUMOS AGRUPADOS POR PRODUCTO =====
  let resumenConsumos = {};
  let cantidadTotalConsumos = 0;

  consumosFiltrados.forEach(function(consumo) {
    const producto = consumo.PRODUCTO;
    const cantidad = consumo.CANTIDAD;

    if (!resumenConsumos[producto]) {
      resumenConsumos[producto] = {
        cantidad: 0
      };
    }

    resumenConsumos[producto].cantidad += cantidad;
    cantidadTotalConsumos += cantidad;
  });

  console.log("üìä Resumen de consumos procesado:");
  console.log(`   ‚Ä¢ Productos consumidos: ${Object.keys(resumenConsumos).length}`);
  console.log(`   ‚Ä¢ Cantidad total consumida: ${cantidadTotalConsumos}`);

  // ===== GENERAR MENSAJE FINAL LIMPIO - SOLO PRODUCTOS RELEVANTES =====
  let mensajeTexto = '';

  if (ventasFiltradas.length === 0) {
    // Sin ventas - SOLO MOSTRAR CONSUMOS SI LOS HAY
    const tituloTexto = tipoConsulta === 'semana' ? 
      'üìä RESUMEN SEMANAL\nüìÖ ' + fechaLegibleCorta(fechaInicioNormalizada) + ' al ' + fechaLegibleCorta(fechaFinNormalizada) :
      'üìä RESUMEN DE VENTAS\nüìÖ ' + fechaLegible(fechaInicioNormalizada);

    mensajeTexto = tituloTexto + '\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n';
    mensajeTexto += '‚ö†Ô∏è No se registraron ventas para este per√≠odo\n\n';

    // MOSTRAR CONSUMOS SI LOS HAY
    if (Object.keys(resumenConsumos).length > 0) {
      mensajeTexto += 'üè† *CONSUMOS PERSONALES:*\n';

      // Ordenar consumos por cantidad descendente
      const consumosArray = Object.entries(resumenConsumos);
      consumosArray.sort(function(a, b) {
        return b[1].cantidad - a[1].cantidad;
      });

      consumosArray.forEach(function(item) {
        const producto = item[0];
        const datos = item[1];
        mensajeTexto += `‚Ä¢ ${producto}: ${formatearNumero(datos.cantidad)} und\n`;
      });

      mensajeTexto += `\nüìä *Total consumido:* ${formatearNumero(cantidadTotalConsumos)} unidades`;
    }

  } else if (tipoConsulta === 'semana' || tipoConsulta === 'mes') {
    // FORMATO LIMPIO PARA SEMANA/MES - SOLO TOTALES Y CONSUMOS
    mensajeTexto = 'üìä RESUMEN SEMANAL\n';
    mensajeTexto += 'üìÖ ' + fechaLegibleCorta(fechaInicioNormalizada) + ' al ' + fechaLegibleCorta(fechaFinNormalizada) + '\n';
    mensajeTexto += '‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n';

    // Ordenar d√≠as por fecha
    const diasOrdenados = Object.keys(resumenPorDia).sort();

    diasOrdenados.forEach(function(fecha) {
      const diaSemana = obtenerDiaSemana(fecha);
      const datos = resumenPorDia[fecha];

      mensajeTexto += 'üìÖ ' + diaSemana + ' ‚Äî ' + formatearPeso(datos.valorTotal) + '\n';
    });

    // Total de ventas
    mensajeTexto += '\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n';
    mensajeTexto += 'üìà TOTAL VENTAS: ' + formatearPeso(valorTotalGeneral) + '\n';

    // AGREGAR CONSUMOS EN REPORTES SEMANALES (SIN INVENTARIO COMPLETO)
    if (Object.keys(resumenConsumos).length > 0) {
      mensajeTexto += '\nüè† *CONSUMOS PERSONALES:*\n';

      const consumosArray = Object.entries(resumenConsumos);
      consumosArray.sort(function(a, b) {
        return b[1].cantidad - a[1].cantidad;
      });

      consumosArray.forEach(function(item) {
        const producto = item[0];
        const datos = item[1];
        mensajeTexto += `‚Ä¢ ${producto}: ${formatearNumero(datos.cantidad)} und\n`;
      });

      mensajeTexto += `\nüìä *Total consumido:* ${formatearNumero(cantidadTotalConsumos)} unidades`;
    }

    // Mostrar solo alerta si hay productos vendidos sin stock
    const productosVendidosAgotados = Object.entries(resumenProductos)
      .filter(item => item[1].stockActual === 0);

    if (productosVendidosAgotados.length > 0) {
      mensajeTexto += `\n\n‚ö†Ô∏è *${productosVendidosAgotados.length} producto(s) vendido(s) sin stock*`;
    }

  } else {
    // FORMATO DETALLADO PARA D√çAS ESPEC√çFICOS - SOLO PRODUCTOS VENDIDOS Y CONSUMIDOS
    mensajeTexto = 'üìä RESUMEN DE VENTAS\n';
    mensajeTexto += 'üìÖ ' + fechaLegible(fechaInicioNormalizada) + '\n';
    mensajeTexto += '‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n';

    // Ordenar productos por cantidad vendida (mayor a menor)
    const productosArray = Object.entries(resumenProductos);
    productosArray.sort(function(a, b) {
      return b[1].cantidad - a[1].cantidad;
    });

    // SECCI√ìN DE PRODUCTOS VENDIDOS CON STOCK (SOLO LOS QUE TIENEN VENTAS)
    const productosConVentas = productosArray.filter(function(item) {
      return item[1].cantidad > 0; // Solo productos que se vendieron
    });

    if (productosConVentas.length > 0) {
      mensajeTexto += 'üì¶ *PRODUCTOS VENDIDOS:*\n';
      productosConVentas.forEach(function(item) {
        const producto = item[0];
        const datos = item[1];

        mensajeTexto += `‚Ä¢ ${producto} ‚Äî ${formatearNumero(datos.cantidad)} und | ${formatearPeso(datos.valorTotal)}`;

        // Solo mostrar stock si hay informaci√≥n relevante
        if (datos.stockActual > 0) {
          mensajeTexto += ` | Stock: ${formatearNumero(datos.stockActual)} und\n`;
        } else if (datos.stockActual === 0) {
          mensajeTexto += ` | ‚ö†Ô∏è Agotado\n`;
        } else {
          mensajeTexto += '\n'; // Sin informaci√≥n de stock
        }
      });
    }

    // Total de ventas
    mensajeTexto += '\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n';
    mensajeTexto += 'üìà TOTAL VENTAS: ' + formatearPeso(valorTotalGeneral) + '\n';

    // SECCI√ìN DE CONSUMOS PERSONALES (SOLO SI LOS HAY)
    if (Object.keys(resumenConsumos).length > 0) {
      mensajeTexto += '\nüè† *CONSUMOS PERSONALES:*\n';

      // Ordenar consumos por cantidad descendente
      const consumosArray = Object.entries(resumenConsumos);
      consumosArray.sort(function(a, b) {
        return b[1].cantidad - a[1].cantidad;
      });

      consumosArray.forEach(function(item) {
        const producto = item[0];
        const datos = item[1];
        mensajeTexto += `‚Ä¢ ${producto} ‚Äî ${formatearNumero(datos.cantidad)} und\n`;
      });

      mensajeTexto += `\nüìä *Total consumido:* ${formatearNumero(cantidadTotalConsumos)} unidades`;
    }
  }

  console.log("‚úÖ Procesamiento completado:");
  console.log(`   ‚Ä¢ Ventas procesadas: ${ventasFiltradas.length}`);
  console.log(`   ‚Ä¢ Consumos procesados: ${consumosFiltrados.length}`);
  console.log(`   ‚Ä¢ Productos √∫nicos en inventario: ${Object.keys(inventarioActual).length}`);
  console.log(`   ‚Ä¢ Productos vendidos: ${Object.keys(resumenProductos).length}`);
  console.log(`   ‚Ä¢ Productos consumidos: ${Object.keys(resumenConsumos).length}`);

  // ===== RETORNAR RESULTADO FINAL =====
  return [{
    json: {
      mensaje_texto: mensajeTexto,
      tipo_consulta: tipoConsulta,
      fecha_inicio: fechaInicioNormalizada,
      fecha_fin: fechaFinNormalizada,
      total_ventas: valorTotalGeneral,
      total_costos: costoTotalGeneral,
      total_utilidades: utilidadTotalGeneral,
      total_productos: cantidadTotalProductos,
      total_transacciones: ventasFiltradas.length,
      resumen_productos: resumenProductos,
      inventario_incluido: Object.keys(inventarioActual).length > 0,
      productos_con_stock: inventarioActual,
      resumen_por_dia: resumenPorDia,
      consumos_incluidos: Object.keys(resumenConsumos).length > 0,
      resumen_consumos: resumenConsumos,
      total_consumos: cantidadTotalConsumos,
      total_registros_consumos: consumosFiltrados.length,
      inventario_stats: {
        productos_inventario: Object.keys(inventarioActual).length,
        fuente_inventario: 'obtener_inventario',
        productos_con_stock: Object.values(inventarioActual).filter(stock => stock > 0).length,
        productos_agotados: Object.values(inventarioActual).filter(stock => stock === 0).length,
        duplicados_removidos: true,
        stock_total: Object.values(inventarioActual).reduce((suma, stock) => suma + stock, 0)
      },
      consumos_stats: {
        productos_consumidos: Object.keys(resumenConsumos).length,
        fuente_consumos: 'obtener_consumos',
        cantidad_total_consumida: cantidadTotalConsumos,
        registros_consumos_filtrados: consumosFiltrados.length
      }
    }
  }];

} catch (error) {
  console.log('‚ùå ERROR GENERAL:', error.message);
  console.log('Stack trace:', error.stack);

  return [{
    json: {
      error: true,
      mensaje_texto: `‚ùå Error generando reporte: ${error.message}`,
      timestamp: new Date().toISOString(),
      error_details: error.stack
    }
  }];
}

// ===== GENERADOR DE MENSAJE DE VENTAS =====
console.log('=== GENERANDO MENSAJE DE CONFIRMACI√ìN ===');

try {
  // Obtener datos del nodo anterior
  const datos = $input.first().json;

  // ===== FUNCIONES AUXILIARES =====

  function formatearNumero(valor) {
    return new Intl.NumberFormat('es-CO').format(valor);
  }

  function formatearPeso(valor) {
    return '$' + formatearNumero(valor);
  }

  function manejarSingularPlural(cantidad, palabra) {
    if (cantidad === 1) {
      if (palabra === 'unidades') {
        return 'unidad';
      }
      if (palabra.endsWith('s')) {
        return palabra.slice(0, -1);
      }
    }
    return palabra;
  }

  function obtenerDiaSemana(fecha) {
    const [dia, mes, a√±o] = fecha.split('-');
    const fechaObj = new Date(a√±o, mes - 1, dia);
    const dias = ['Domingo', 'Lunes', 'Martes', 'Mi√©rcoles', 'Jueves', 'Viernes', 'S√°bado'];
    return dias[fechaObj.getDay()];
  }

  function fechaLegible(fecha) {
    const [dia, mes, a√±o] = fecha.split('-');
    const meses = ['enero', 'febrero', 'marzo', 'abril', 'mayo', 'junio',
                   'julio', 'agosto', 'septiembre', 'octubre', 'noviembre', 'diciembre'];
    const diaSemana = obtenerDiaSemana(fecha);
    return `${diaSemana}, ${parseInt(dia)} de ${meses[parseInt(mes)-1]} de ${a√±o}`;
  }

  function fechaLegibleCorta(fecha) {
    const [dia, mes] = fecha.split('-');
    return `${dia}/${mes}`;
  }

  // ===== CONSTRUIR MENSAJE =====
  let mensaje = '';

  if (datos.tipoConsulta === 'semana' || datos.tipoConsulta === 'mes') {
    mensaje = 'üìä RESUMEN SEMANAL\n';
    mensaje += `üìÖ ${fechaLegibleCorta(datos.fechaInicio)} al ${fechaLegibleCorta(datos.fechaFin)}\n`;
  } else {
    mensaje = 'üìä RESUMEN DE VENTAS\n';
    mensaje += `üìÖ ${fechaLegible(datos.fechaInicio)}\n`;
  }

  mensaje += '‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n';

  // ===== SECCI√ìN DE VENTAS =====
  if (datos.hay_ventas) {
    mensaje += 'üì¶ *PRODUCTOS VENDIDOS:*\n';

    // Lista de productos que no muestran stock y tienen orden fijo
    const productosSinStock = ["Arepas de QUESO", "Arepa BURGUER", "Chorizos"];

    const productosVentas = Object.entries(datos.ventas.productos);

    // Separar excluidos y normales
    const excluidos = productosSinStock
      .map(nombre => productosVentas.find(([producto]) => producto === nombre))
      .filter(Boolean); // elimina los que no existan en ventas

    const normales = productosVentas
      .filter(([producto]) => !productosSinStock.includes(producto))
      .sort(([,a], [,b]) => b.cantidad - a.cantidad);

    const productosOrdenados = [...excluidos, ...normales];

    productosOrdenados.forEach(([producto, info]) => {
      mensaje += `‚Ä¢ ${formatearNumero(info.cantidad)} ${producto} | ${formatearPeso(info.valor)}`;

      if (!productosSinStock.includes(producto)) {
        if (info.stock > 0) {
          const stockTexto = manejarSingularPlural(info.stock, 'unidades');
          mensaje += ` | Stock: ${formatearNumero(info.stock)} ${stockTexto}\n`;
        } else if (info.stock === 0) {
          mensaje += ' | ‚ö†Ô∏è Agotado\n';
        } else {
          mensaje += '\n';
        }
      } else {
        mensaje += '\n';
      }
    });

    mensaje += '\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n';
    mensaje += `üìà TOTAL VENTAS: ${formatearPeso(datos.ventas.total)}\n\n`;
  } else {
    mensaje += '‚ö†Ô∏è No se registraron ventas para este per√≠odo\n\n';
  }

  // ===== SECCI√ìN DE COMPRAS =====
  if (datos.hay_compras) {
    mensaje += 'üõí *COMPRAS:*\n';
    const productosCompras = Object.entries(datos.compras.productos)
      .sort(([,a], [,b]) => b.cantidad - a.cantidad);

    productosCompras.forEach(([producto, info]) => {
      const factorTexto = manejarSingularPlural(info.cantidad, info.factor);
      mensaje += `‚Ä¢ ${formatearNumero(info.cantidad)} ${factorTexto} ${producto}\n`;
    });
    mensaje += '\n';
  }

  // ===== SECCI√ìN DE CONSUMOS PERSONALES =====
  if (datos.hay_consumos) {
    mensaje += 'üè† *CONSUMOS PERSONALES:*\n';
    const productosConsumos = Object.entries(datos.consumos.productos)
      .sort(([,a], [,b]) => b - a);

    productosConsumos.forEach(([producto, cantidad]) => {
      mensaje += `‚Ä¢ ${formatearNumero(cantidad)} ${producto}\n`;
    });
  }

  console.log('‚úÖ Mensaje generado exitosamente');
  console.log(`   ‚Ä¢ Longitud: ${mensaje.length} caracteres`);
  console.log(`   ‚Ä¢ Productos vendidos: ${datos.ventas.cantidad_productos}`);
  console.log(`   ‚Ä¢ Total ventas: ${formatearPeso(datos.ventas.total)}`);

  return [{
    json: {
      mensaje_texto: mensaje,
      estadisticas: {
        total_ventas: datos.ventas.total,
        productos_vendidos: datos.ventas.cantidad_productos,
        productos_consumidos: datos.consumos.cantidad_productos,
        productos_comprados: datos.compras.cantidad_productos,
        hay_ventas: datos.hay_ventas,
        hay_consumos: datos.hay_consumos,
        hay_compras: datos.hay_compras
      }
    }
  }];

} catch (error) {
  console.log('‚ùå ERROR generando mensaje:', error.message);

  return [{
    json: {
      error: true,
      mensaje_texto: `‚ùå Error generando mensaje: ${error.message}`,
      timestamp: new Date().toISOString()
    }
  }];
}

// ===== EXTRACTOR SIMPLE DE DATOS DE VENTAS =====
console.log('=== EXTRAYENDO DATOS PARA MENSAJE DE CONFIRMACI√ìN ===');

try {
  // ===== OBTENER FECHAS DEL MENSAJE INTERPRETADO =====
  const consulta = $('Interpretar_mensaje').item.json;
  const fechaInicio = consulta.fechaInicio;
  const fechaFin = consulta.fechaFin;
  const tipoConsulta = consulta.queryType;

  console.log(`üìÖ Consultando desde: ${fechaInicio} hasta: ${fechaFin} (${tipoConsulta})`);

  // ===== FUNCI√ìN PARA NORMALIZAR FECHAS =====
  function normalizarFecha(fecha) {
    if (!fecha) return null;

    // DD-MM-YYYY -> mantener
    if (fecha.match(/^\d{1,2}-\d{1,2}-\d{4}$/)) {
      const [dia, mes, a√±o] = fecha.split('-');
      return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + a√±o;
    }

    // DD/MM/YYYY -> DD-MM-YYYY
    if (fecha.includes('/')) {
      const [dia, mes, a√±o] = fecha.split(',')[0].split('/');
      return dia.padStart(2, '0') + '-' + mes.padStart(2, '0') + '-' + a√±o;
    }

    return null;
  }

  // ===== FILTRAR DATOS POR FECHA =====
  function filtrarPorFecha(datos, fechaField) {
    const fechaInicioNorm = normalizarFecha(fechaInicio);
    const fechaFinNorm = normalizarFecha(fechaFin);

    return datos.filter(item => {
      const fechaItem = normalizarFecha(item[fechaField]);
      return fechaItem >= fechaInicioNorm && fechaItem <= fechaFinNorm;
    });
  }

  // ===== EXTRAER VENTAS =====
  const ventasRaw = $('ventas_solicitadas').all().map(item => item.json);
  const ventasFiltradas = filtrarPorFecha(ventasRaw, 'FECHA');

  // Resumir ventas por producto
  const ventasResumen = {};
  let totalVentas = 0;

  ventasFiltradas.forEach(venta => {
    const producto = venta.PRODUCTO;
    const cantidad = parseInt(venta.CANTIDAD) || 0;
    const valorTotal = parseFloat(venta.VALOR_TOTAL) || 0;

    if (!ventasResumen[producto]) {
      ventasResumen[producto] = { cantidad: 0, valor: 0 };
    }

    ventasResumen[producto].cantidad += cantidad;
    ventasResumen[producto].valor += valorTotal;
    totalVentas += valorTotal;
  });

  // ===== EXTRAER INVENTARIO =====
  const inventarioRaw = $('obtener_inventario').all().map(item => item.json);
  const inventario = {};

  inventarioRaw.forEach(item => {
    const producto = item.PRODUCTO || item.producto;
    const stock = parseInt(item.STOCK_ACTUAL_UNIDADES || item.stock_actual_unidades) || 0;

    if (producto && producto.trim()) {
      inventario[producto.trim()] = stock;
    }
  });

  // ===== EXTRAER CONSUMOS =====
  const consumosRaw = $('obtener_consumos').all().map(item => item.json);
  const consumosFiltrados = filtrarPorFecha(consumosRaw, 'FECHA');

  const consumosResumen = {};
  let totalConsumos = 0;

  consumosFiltrados.forEach(consumo => {
    const producto = consumo.PRODUCTO;
    const cantidad = parseInt(consumo.CANTIDAD) || 0;

    if (!consumosResumen[producto]) {
      consumosResumen[producto] = 0;
    }

    consumosResumen[producto] += cantidad;
    totalConsumos += cantidad;
  });

  // ===== EXTRAER COMPRAS =====
  const comprasRaw = $('registroEntradaInventario').all().map(item => item.json);
  const comprasFiltradas = filtrarPorFecha(comprasRaw, 'FECHA');

  const comprasResumen = {};
  let totalCompras = 0;

  comprasFiltradas.forEach(compra => {
    const producto = compra.PRODUCTO;
    const cantidad = parseInt(compra.CANTIDAD) || 0;
    const factor = compra.FACTOR_UNIDADES || 'unidades';

    if (!comprasResumen[producto]) {
      comprasResumen[producto] = { cantidad: 0, factor };
    }

    comprasResumen[producto].cantidad += cantidad;
    totalCompras += cantidad;
  });

  // ===== BUSCAR STOCK DE PRODUCTOS VENDIDOS =====
  function buscarStock(nombreProducto) {
    if (!nombreProducto) return 0;

    const nombre = nombreProducto.trim().toLowerCase();

    // B√∫squeda exacta
    for (const [producto, stock] of Object.entries(inventario)) {
      if (producto.toLowerCase() === nombre) {
        return stock;
      }
    }

    // B√∫squeda parcial
    for (const [producto, stock] of Object.entries(inventario)) {
      if (producto.toLowerCase().includes(nombre) || nombre.includes(producto.toLowerCase())) {
        return stock;
      }
    }

    return 0;
  }

  // Agregar stock a productos vendidos
  Object.keys(ventasResumen).forEach(producto => {
    ventasResumen[producto].stock = buscarStock(producto);
  });

  // ===== DATOS FINALES SIMPLIFICADOS =====
  const datosExtraidos = {
    // Metadatos de consulta
    fechaInicio: normalizarFecha(fechaInicio),
    fechaFin: normalizarFecha(fechaFin),
    tipoConsulta: tipoConsulta,

    // Datos principales
    ventas: {
      productos: ventasResumen,
      total: totalVentas,
      cantidad_productos: Object.keys(ventasResumen).length,
      total_transacciones: ventasFiltradas.length
    },

    consumos: {
      productos: consumosResumen,
      total_cantidad: totalConsumos,
      cantidad_productos: Object.keys(consumosResumen).length
    },

    compras: {
      productos: comprasResumen,
      total_cantidad: totalCompras,
      cantidad_productos: Object.keys(comprasResumen).length
    },

    inventario: {
      productos: inventario,
      total_productos: Object.keys(inventario).length
    },

    // Flags √∫tiles para el mensaje
    hay_ventas: Object.keys(ventasResumen).length > 0,
    hay_consumos: Object.keys(consumosResumen).length > 0,
    hay_compras: Object.keys(comprasResumen).length > 0,
    hay_productos_agotados: Object.values(ventasResumen).some(v => v.stock === 0)
  };

  console.log('‚úÖ Datos extra√≠dos exitosamente:');
  console.log(`   ‚Ä¢ Ventas: ${datosExtraidos.ventas.cantidad_productos} productos, $${datosExtraidos.ventas.total.toLocaleString()}`);
  console.log(`   ‚Ä¢ Consumos: ${datosExtraidos.consumos.cantidad_productos} productos, ${datosExtraidos.consumos.total_cantidad} unidades`);
  console.log(`   ‚Ä¢ Compras: ${datosExtraidos.compras.cantidad_productos} productos, ${datosExtraidos.compras.total_cantidad} items`);
  console.log(`   ‚Ä¢ Inventario: ${datosExtraidos.inventario.total_productos} productos`);

  return [{
    json: datosExtraidos
  }];

} catch (error) {
  console.log('‚ùå ERROR:', error.message);

  return [{
    json: {
      error: true,
      mensaje: `Error extrayendo datos: ${error.message}`,
      timestamp: new Date().toISOString()
    }
  }];
}
