// Capturamos la respuesta de Gemini y limpiamos
let respuestaGemini = String($input.first().json.output || '').trim();
respuestaGemini = respuestaGemini.replace(/```json\n?/g, '').replace(/\n?```/g, '').trim();

// Parseamos el JSON
let datosExtraidos;
try {
  datosExtraidos = JSON.parse(respuestaGemini);
} catch (error) {
  return [{ json: { error: "No se pudo procesar la respuesta de Gemini" } }];
}

// ======= NUEVA L√ìGICA: PROCESAR TODOS LOS PRODUCTOS =======
let productosAProcesar = [];

// Verificamos si es un array o un objeto √∫nico
if (Array.isArray(datosExtraidos)) {
  // Es un array, procesar todos los elementos
  productosAProcesar = datosExtraidos;
  console.log(`üì¶ Procesando ${datosExtraidos.length} productos del array`);
} else {
  // Es un objeto √∫nico, convertir a array
  productosAProcesar = [datosExtraidos];
  console.log(`üì¶ Procesando 1 producto √∫nico`);
}

// Array para almacenar todos los resultados
const resultados = [];

// Procesar cada producto individualmente
productosAProcesar.forEach((itemData, index) => {
  console.log(`üîç Procesando producto ${index + 1}: ${itemData.PRODUCTO}`);

  // Preparamos los datos y validamos
  const fila = {
    PRODUCTO: itemData.PRODUCTO,
    UNIDAD_MEDIDA: itemData.UNIDAD_MEDIDA || "",
    CANTIDAD: parseFloat(itemData.CANTIDAD),
    PRECIO: parseFloat(itemData.PRECIO || 0),
    FECHA: new Date().toLocaleString('es-CO', { timeZone: 'America/Bogota' }),
    errores: []
  };

  // Validaciones espec√≠ficas para cada producto
  if (!fila.PRODUCTO || fila.PRODUCTO === "" || fila.PRODUCTO === "null") {
    fila.errores.push("‚ùå No se pudo identificar el PRODUCTO");
  }
  if (!fila.CANTIDAD || fila.CANTIDAD <= 0 || isNaN(fila.CANTIDAD)) {
    fila.errores.push("‚ùå La CANTIDAD debe ser mayor a 0");
  }

  // Agregar este producto al array de resultados
  resultados.push({ json: fila });

  // Log del resultado
  if (fila.errores.length === 0) {
    console.log(`‚úÖ Producto ${index + 1} procesado: ${fila.PRODUCTO} - ${fila.CANTIDAD} ${fila.UNIDAD_MEDIDA}`);
  } else {
    console.log(`‚ùå Producto ${index + 1} con errores: ${fila.errores.join(', ')}`);
  }
});

// Log final
console.log(`üìä Resumen: ${resultados.length} productos procesados`);

// Retornar TODOS los productos procesados
return resultados;

// Diccionario de factores por producto (normalizado a min√∫sculas)
const factores = {
  "cerveza poker en botella 330ml": 30,
  "cerveza aguila en botella 330ml": 30,
  "gaseosa manzana postobon 350ml": 24,
  "botella agua 600ml": 20,
  "coca cola 350ml": 24,
  "gaseosa quatro 2.5 lts": 8,
  "gaseosa quatro 350ml": 24,
  "gaseosa quatro 250ml": 24,
  "de todito": 15,
  "aguardiente llanero por botella": 12,
  "aguardiente llanero por 1/2": 12
};

try {
  // ======= OBTENER DATOS DE M√öLTIPLES COMPRAS =======
  const comprasData = $("Procesar_compra").all();
  console.log(`üì¶ Procesando ${comprasData.length} compras`);

  // Validaci√≥n inicial
  if (!comprasData || comprasData.length === 0) {
    throw new Error("No hay datos de compras para procesar");
  }

  // ======= OBTENER INVENTARIO ACTUAL =======
  const inventarioItems = $input.all(); // Todos los items del inventario
  console.log(`üìä Items de inventario disponibles: ${inventarioItems.length}`);

  // Crear un mapa del inventario por producto para b√∫squeda r√°pida
  const inventarioMap = {};
  inventarioItems.forEach(item => {
    if (item.json && item.json.PRODUCTO) {
      const producto = item.json.PRODUCTO.trim();
      inventarioMap[producto] = item.json;
    }
  });

  console.log(`üìã Productos en inventario:`, Object.keys(inventarioMap));

  // ======= PROCESAR CADA COMPRA INDIVIDUALMENTE =======
  const resultados = [];

  comprasData.forEach((compraItem, index) => {
    try {
      const compra = compraItem.json;
      console.log(`\nüîç Procesando compra ${index + 1}: ${compra.PRODUCTO}`);

      // Validaci√≥n de datos de compra
      if (!compra || !compra.PRODUCTO) {
        throw new Error(`Compra ${index + 1}: Datos inv√°lidos - falta informaci√≥n del producto`);
      }

      // Buscar este producto espec√≠fico en el inventario
      const producto = compra.PRODUCTO.trim();
      const inventario = inventarioMap[producto] || {}; // Buscar por nombre exacto
      const productoExiste = inventario.PRODUCTO ? true : false;

      console.log(`   üìã Producto: ${producto}`);
      console.log(`   üìç Existe en inventario: ${productoExiste}`);

      // Procesar datos del producto
      const productoLower = producto.toLowerCase();
      const cantidadCompra = Number(compra.CANTIDAD) || 0;
      const precioCompra = Number(compra.PRECIO) || 0;

      // Validaciones adicionales
      if (cantidadCompra <= 0) {
        throw new Error(`${producto}: La cantidad debe ser mayor a 0`);
      }

      if (precioCompra < 0) {
        throw new Error(`${producto}: El precio no puede ser negativo`);
      }

      // Factor de unidades (primero buscar en diccionario, luego en inventario)
      const factorUnidades = factores[productoLower] || Number(inventario.FACTOR_UNIDADES) || 1;

      // Stock actual
      const stockActual = Number(inventario.STOCK_ACTUAL_UNIDADES) || 0;

      // Calcular unidades ingresadas
      const unidadesIngresadas = cantidadCompra * factorUnidades;

      // Calcular nuevo stock
      const nuevoStock = stockActual + unidadesIngresadas;

      // Fecha actual en zona horaria de Colombia
      const fechaActual = new Date().toLocaleString('es-CO', { 
        timeZone: 'America/Bogota',
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
      });

      // Log del procesamiento
      console.log(`   üìä Stock: ${stockActual} ‚Üí ${nuevoStock} (+${unidadesIngresadas})`);
      console.log(`   üîß Factor: ${factorUnidades} | Acci√≥n: actualizar`);

      // Crear resultado para este producto
      const resultado = {
        json: {
          PRODUCTO: producto,
          UNIDAD_MEDIDA: compra.UNIDAD_MEDIDA || "cajas",
          FACTOR_UNIDADES: factorUnidades,
          CANTIDAD: cantidadCompra,
          STOCK_ACTUAL_UNIDADES: nuevoStock,
          PRECIO_ULTIMA_COMPRA: precioCompra,
          FECHA_ULTIMA_COMPRA: fechaActual,
          UNIDADES_INGRESADAS: unidadesIngresadas,
          STOCK_ANTERIOR: stockActual,
          accion: "actualizar",
          // Campos adicionales para tracking
          row_number: inventario.row_number || null,
          procesado_en_lote: true,
          indice_compra: index + 1
        }
      };

      resultados.push(resultado);
      console.log(`   ‚úÖ Producto procesado exitosamente`);

    } catch (error) {
      console.error(`   ‚ùå Error procesando compra ${index + 1}:`, error.message);

      // Agregar resultado de error para este producto espec√≠fico
      resultados.push({
        json: {
          error: true,
          mensaje: error.message,
          producto_con_error: compraItem.json?.PRODUCTO || 'Desconocido',
          indice_compra: index + 1,
          timestamp: new Date().toISOString()
        }
      });
    }
  });

  // ======= RESUMEN FINAL =======
  const exitosos = resultados.filter(r => !r.json.error).length;
  const conError = resultados.filter(r => r.json.error).length;

  console.log(`\nüìä RESUMEN FINAL:`);
  console.log(`   Total compras: ${comprasData.length}`);
  console.log(`   Procesadas exitosamente: ${exitosos}`);
  console.log(`   Con errores: ${conError}`);

  // Retornar TODOS los resultados
  return resultados;

} catch (error) {
  console.error("‚ùå Error general procesando compras:", error.message);

  // Retornar error estructurado general
  return [{
    json: {
      error: true,
      mensaje: `Error general: ${error.message}`,
      tipo_error: "error_general",
      timestamp: new Date().toISOString()
    }
  }];
}

// C√≥digo para generar mensaje de confirmaci√≥n de inventario
// USAR DATOS DE CALCULAR_STOCK (que est√°n completos)
const inputData = $('Calcular_Stock').all();

// Funci√≥n para formatear n√∫meros con separadores de miles
function formatearNumero(numero) {
  return new Intl.NumberFormat('es-CO').format(numero);
}

// Funci√≥n para formatear precios
function formatearPrecio(precio) {
  if (precio === 0 || precio === null || precio === undefined) {
    return "No registrado";
  }
  return "$" + formatearNumero(precio);
}

// Funci√≥n para manejar singular/plural de unidades de medida
function pluralizar(cantidad, unidad) {
  const cantidadNum = parseInt(cantidad);

  if (cantidadNum === 1) {
    // Convertir a singular
    if (unidad === "cajas") return "caja";
    if (unidad === "pacas") return "paca";
    if (unidad === "unidades") return "unidad";
    if (unidad === "botellas") return "botella";
    if (unidad === "litros") return "litro";
    return unidad; // Si no coincide, devolver original
  }

  return unidad; // Plural (original)
}

// Procesar los datos
const productos = [];
let totalProductosActualizados = 0;
let totalUnidadesIngresadas = 0;

inputData.forEach((item, index) => {
  // Los datos vienen directamente en item.json desde Calcular_Stock
  const data = item.json;

  // Verificar si hay errores
  if (data.error) {
    productos.push({
      nombre: data.producto_con_error || "Producto desconocido",
      error: data.mensaje,
      esError: true
    });
    return;
  }

  // Extraer valores (ahora directamente, ya que sabemos que est√°n disponibles)
  const cantidad = parseInt(data.CANTIDAD);
  const stockAnterior = parseInt(data.STOCK_ANTERIOR);
  const stockNuevo = parseInt(data.STOCK_ACTUAL_UNIDADES);
  const unidadesIngresadas = parseInt(data.UNIDADES_INGRESADAS);
  const precio = parseFloat(data.PRECIO_ULTIMA_COMPRA) || 0;
  const producto = data.PRODUCTO;
  const unidadMedida = data.UNIDAD_MEDIDA;

  // Procesar producto exitoso
  const unidadFormateada = pluralizar(cantidad, unidadMedida);

  productos.push({
    nombre: producto,
    cantidad: cantidad,
    unidad: unidadFormateada,
    stockAnterior: stockAnterior,
    stockNuevo: stockNuevo,
    unidadesIngresadas: unidadesIngresadas,
    factor: parseInt(data.FACTOR_UNIDADES),
    precio: precio,
    accion: data.accion,
    esError: false
  });

  totalProductosActualizados++;
  totalUnidadesIngresadas += unidadesIngresadas;
});

// Generar mensaje de confirmaci√≥n
let mensaje = "";

// --- INICIO DE LOS CAMBIOS ---
// Mensaje para cuando no hay productos que procesar
if (productos.length === 0) {
  mensaje = "‚ö†Ô∏è El inventario no tiene productos para procesar.";
} else {
  // Encabezado con nuevo formato
  mensaje += "‚úÖ *Informe de actualizaci√≥n de inventario*\n";
  mensaje += `üìÖ ${new Date().toLocaleDateString('es-CO', { 
    timeZone: 'America/Bogota',
    weekday: 'long',
    year: 'numeric', 
    month: 'long', 
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  })}`.replace(/^\w/, c => c.toUpperCase()) + "\n\n";

  // Listar productos en formato m√°s detallado
  mensaje += "*Detalles de los productos procesados:*\n\n";

  productos.forEach((producto, index) => {
    if (producto.esError) {
      mensaje += `‚ùå *${producto.nombre}* - Error: ${producto.error}\n`;
    } else {
      mensaje += `üì¶ *${producto.nombre}*:\n`;
      mensaje += `- Cantidad: ${producto.cantidad} ${producto.unidad}\n`;
      mensaje += `- Nuevo stock: ${formatearNumero(producto.stockNuevo)} unidades\n\n`;
    }
  });
}
// --- FIN DE LOS CAMBIOS ---

// Crear versi√≥n de texto plano (sin markdown)
let mensajeTextoPlano = mensaje
  .replace(/\*/g, "") // Quitar asteriscos de markdown
  .replace(/‚îÅ/g, "-"); // Reemplazar caracteres especiales

// Retornar resultado
const resultado = {
  json: {
    mensaje_html: mensaje, // Con formato markdown para Telegram
    mensaje_texto: mensajeTextoPlano, // Texto plano
    parse_mode: "Markdown", // Para Telegram
    total_productos: productos.length,
    productos_exitosos: totalProductosActualizados,
    productos_con_error: productos.filter(p => p.esError).length,
    total_unidades_ingresadas: totalUnidadesIngresadas,
    fecha_procesamiento: new Date().toISOString(),
    resumen_productos: productos,
    tipo_operacion: "actualizacion_inventario"
  }
};

console.log(`üìä Mensaje de confirmaci√≥n generado:`);
console.log(`   - Productos procesados: ${productos.length}`);
console.log(`   - Productos exitosos: ${totalProductosActualizados}`);
console.log(`   - Total unidades: ${totalUnidadesIngresadas}`);

return [resultado];
