// NODO CODE - VERIFICADOR DE PRODUCTOS
// Input: todas las filas de Google Sheets + mensaje desde IF1

function procesarMensaje() {
  // Mensaje desde IF1
  const mensaje = $('IF1').first().json.final_message;

  // Todas las filas de Google Sheets
  const todasLasFilas = $input.all().map(item => item.json);

  // Crear mapa de apodos -> producto
  const mapaApodos = {};
  todasLasFilas.forEach(fila => {
    const producto = fila["NOMBRE DE PRODUCTO"];
    const apodos = fila["REFERENCIAS_Y_APODOS"];
    if (apodos && producto) {
      String(apodos)
        .split(',')
        .map(a => a.trim())
        .filter(a => a)
        .forEach(apodo => {
          mapaApodos[apodo] = producto;
        });
    }
  });

  // Ordenar apodos por longitud (más largos primero para evitar solapamientos tipo "pan" y "panela")
  const apodosOrdenados = Object.keys(mapaApodos).sort((a, b) => b.length - a.length);

  // Buscar ocurrencias en el mensaje original
  let reemplazos = [];
  apodosOrdenados.forEach(apodo => {
    const producto = mapaApodos[apodo];
    const apodoEscapado = apodo.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
    const regex = new RegExp('\\b' + apodoEscapado + '\\b', 'g');
    let match;
    while ((match = regex.exec(mensaje)) !== null) {
      reemplazos.push({ start: match.index, end: match.index + match[0].length, producto });
    }
  });

  // Aplicar reemplazos de izquierda a derecha
  reemplazos.sort((a, b) => a.start - b.start);
  let mensajeProcesado = "";
  let lastIndex = 0;
  reemplazos.forEach(r => {
    mensajeProcesado += mensaje.slice(lastIndex, r.start) + r.producto;
    lastIndex = r.end;
  });
  mensajeProcesado += mensaje.slice(lastIndex);

  return [{
    json: {
      mensaje_original: mensaje,
      mensaje_procesado: mensajeProcesado,
      apodos_detectados: Object.keys(mapaApodos),
      total_filas_procesadas: todasLasFilas.length,
      mapa_completo: mapaApodos
    }
  }];
}

// Ejecutar función
return procesarMensaje();

