function normalizar(s) {
  if (s === undefined || s === null) return "";
  return String(s)
    .trim()
    .toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")
    .replace(/\s+/g, " ");
}

function levenshtein(a, b) {
  const m = a.length, n = b.length;
  if (m === 0) return n;
  if (n === 0) return m;
  const dp = Array.from({ length: m + 1 }, () => new Array(n + 1).fill(0));
  for (let i = 0; i <= m; i++) dp[i][0] = i;
  for (let j = 0; j <= n; j++) dp[0][j] = j;
  for (let i = 1; i <= m; i++) {
    for (let j = 1; j <= n; j++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      dp[i][j] = Math.min(
        dp[i - 1][j] + 1,
        dp[i][j - 1] + 1,
        dp[i - 1][j - 1] + cost
      );
    }
  }
  return dp[m][n];
}

function similitud(a, b) {
  if (!a && !b) return 1;
  if (!a || !b) return 0;
  const dist = levenshtein(a, b);
  return 1 - dist / Math.max(a.length, b.length);
}

// Extraer todas las palabras entre comillas
function extraerMarcados(texto) {
  if (!texto) return [];
  const matches = String(texto).match(/"([^"]+)"/g);
  if (!matches) return [];
  return matches.map(m => m.replace(/"/g, ""));
}

const UMBRAL_ACEPTAR = 0.85;
const UMBRAL_SUGERIR = 0.60;

let resultados = [];

// Traemos todos los apodos desde el nodo obtenerApodos
const apodos = $items("obtenerApodos");

for (const item of items) {
  // 👇 TOMAR EXACTAMENTE LO QUE LLEGA
  const mensajeOriginal = item.json.producto_detectado || "";
  let mensajeProcesado = mensajeOriginal;

  const detectados = extraerMarcados(mensajeOriginal);

  // Si no hay productos marcados, devolver el mensaje tal como llega
  if (detectados.length === 0) {
    resultados.push({
      json: {
        mensaje_final: mensajeOriginal,
        sin_productos: true
      }
    });
    continue;
  }

  let hayReemplazos = false;
  let haySugerencias = false;
  let sugerenciasData = null;

  for (const detectado of detectados) {
    const entrada = normalizar(detectado);

    if (!entrada) continue;

    // 1) Coincidencia exacta
    let exacto = apodos.find(f => normalizar(f.json.APODO) === entrada);
    if (exacto) {
      // REEMPLAZAR en el mensaje: "producto" -> producto_encontrado
      const regex = new RegExp(`"${detectado}"`, 'g');
      mensajeProcesado = mensajeProcesado.replace(regex, exacto.json.PRODUCTO);
      hayReemplazos = true;
      continue;
    }

    // 2) Calcular similitud con todos los apodos
    let comparaciones = [];
    for (const fila of apodos) {
      const apodo = normalizar(fila.json.APODO);
      const score = similitud(entrada, apodo);
      comparaciones.push({
        producto: fila.json.PRODUCTO,
        apodo,
        score
      });
    }

    comparaciones.sort((a, b) => b.score - a.score);
    const top3 = comparaciones.slice(0, 3);

    // 3) Decisión
    if (top3[0] && top3[0].score >= UMBRAL_ACEPTAR) {
      // REEMPLAZAR en el mensaje: "producto" -> producto_encontrado
      const regex = new RegExp(`"${detectado}"`, 'g');
      mensajeProcesado = mensajeProcesado.replace(regex, top3[0].producto);
      hayReemplazos = true;
    } else if (top3[0] && top3[0].score >= UMBRAL_SUGERIR) {
      // SUGERENCIAS - no reemplazar, enviar sugerencias
      haySugerencias = true;
      sugerenciasData = {
        apodo_usado: detectado,
        sugerencias: top3.map((r, i) => ({
          opcion: i + 1,
          producto: r.producto,
          apodo: r.apodo,
          confianza: Number(r.score.toFixed(3))
        }))
      };
      break; // Solo una sugerencia por mensaje
    } else {
      // SIN MATCH
      resultados.push({
        json: {
          mensaje_final: `❌ No encontré "${detectado}" en el menú.\n¿Podrías verificar el nombre del producto?`,
          sin_coincidencia: true,
          apodo_usado: detectado
        }
      });
      return resultados; // Salir si hay productos sin match
    }
  }

  // DEVOLVER RESULTADO SEGÚN LO QUE PASÓ
  if (haySugerencias && sugerenciasData) {
    // Hay sugerencias
    let opciones = sugerenciasData.sugerencias.map(s => {
      let numEmoji = ["1️⃣","2️⃣","3️⃣","4️⃣","5️⃣"][s.opcion - 1] || `${s.opcion})`;
      return `${numEmoji} ${s.producto}`;
    }).join("\n");

    resultados.push({
      json: {
        mensaje_final: `🤔 No estuve 100% seguro con "${sugerenciasData.apodo_usado}".\n¿Te refieres a alguna de estas opciones?\n\n${opciones}\n\n💬 Responde con el número`,
        requiere_confirmacion: true,
        sugerencias: sugerenciasData.sugerencias,
        apodo_usado: sugerenciasData.apodo_usado
      }
    });
  } else {
    // Productos encontrados o mensaje sin cambios
    resultados.push({
      json: {
        mensaje_final: mensajeProcesado,
        productos_reemplazados: hayReemplazos
      }
    });
  }
}

return resultados;

// NODO armaMensaje - VERSIÓN CORREGIDA
const resultados = $input.all();

if (resultados.length === 0) {
  return [{ json: { mensaje: "No hay productos para procesar" } }];
}

let mensaje = "";

// Procesar cada resultado
for (const r of resultados) {
  // ✅ Si hay mensaje_final y productos_reemplazados = true
  if (r.json.productos_reemplazados === true && r.json.mensaje_final) {
    mensaje = `✅ Producto detectado: ${r.json.mensaje_final}`;
    break;
  }

  // ✅ Si requiere confirmación (sugerencias)
  else if (r.json.requiere_confirmacion === true) {
    mensaje = r.json.mensaje_final; // Ya viene formateado
    break;
  }

  // ✅ Si no encontró coincidencia
  else if (r.json.sin_coincidencia === true) {
    mensaje = r.json.mensaje_final; // Ya viene formateado
    break;
  }

  // ✅ Si no hay productos marcados
  else if (r.json.sin_productos === true) {
    mensaje = "No se detectaron productos válidos en tu mensaje.";
    break;
  }
}

if (!mensaje) {
  mensaje = "No se detectaron productos válidos en tu mensaje.";
}

return [{ json: { mensaje } }];

